--
-- Estructura de tabla para la tabla `destinations`
--

CREATE TABLE IF NOT EXISTS `destinations` (
  `id` int(10) unsigned NOT NULL auto_increment,
  `tipo` enum('Cuba','Yucatan') default 'Cuba',
  `nombre` varchar(255) NOT NULL,
  `slug` varchar(255) NOT NULL,
  `subtitulo_ita` text,
  `subtitulo_esp` text,
  `descripcion_ita` text,
  `descripcion_esp` text,
  `activo` tinyint(1) default '1',
  `comment_count` int(11) default '0',
  `destinationimg_count` int(11) default '0',
  `created` datetime default NULL,
  PRIMARY KEY  (`id`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 AUTO_INCREMENT=20 ;

--
-- Volcado de datos para la tabla `destinations`
--

INSERT INTO `destinations` (`id`, `tipo`, `nombre`, `slug`, `subtitulo_ita`, `subtitulo_esp`, `descripcion_ita`, `descripcion_esp`, `activo`, `comment_count`, `destinationimg_count`, `created`) VALUES
(1, 'Cuba', 'Trinidad', '1_trinidad', '<p>Non &eacute; vero che qui il tempo si sia fermato, come dicono tutte quante e orgogliose le principali agenzie di viaggio.</p>', '<p>No es cierto que el tiempo se ha detenido aqu&iacute;, como dicen todas y orgullosas las agencias de viajes.</p>', '<p>Credetemi, in realt&aacute; il tempo &eacute; curvo nella Trinidad coloniale, si muove senza posa ma in tondo,&nbsp; obbliga eternamente la Citt&aacute; a passare e ripassare sempre dagli stessi punti, portandosi in groppa gente e vie di pietra, case antiche e silenziosi patios, e carica di musica e&nbsp; balli la notte, splendida sempre.</p>\r\n<p>Trinidad funziona come un magnete, gemma lucente che richiama a s&eacute; continuamente le attenzioni di tutti, meritate.</p>\r\n<p>Sedetevi di pomeriggio tardi al bar della Escalinata, quando comincia a fare un po&rsquo; meno caldo e la luce diventa color di rosa e color di rosa e giallo e azzurro sono case e piazze,&nbsp; e guardate gi&uacute; verso la Citt&aacute;, verso la spiaggia lontana, poi scriveteci e raccontateci tutto quanto.</p>\r\n<p>&nbsp;</p>\r\n<h2 align="center"><strong>Informazioni sulla Citt&aacute;, e alcuni posti da vedere</strong></h2>\r\n<p><strong>&nbsp;</strong></p>\r\n<p>Fondata da Diego Velasquez nel 1515, con il nome di Villa de la Sant&iacute;sima Trinidad.</p>\r\n<p>Patrimonio dell&rsquo;Umanit&aacute; secondo l&rsquo;Unesco.</p>\r\n<p>Settantamila abitanti circa e in continua espansione, arriva gente da tutto il Paese, da Sancti Spiritus, Cienfuegos, dall&rsquo;Habana, dall&rsquo;oriente dell&rsquo;Isola.</p>\r\n<p>Trinidad &eacute; Citt&aacute;-Museo di stile coloniale, posizionata fra il mare e il gruppo montagnoso dell&rsquo;Escambray, ben conservata nelle zone del centro e di collina,&nbsp; quest&rsquo;ultima chiamata non a caso Zona Monumento.</p>\r\n<p>Qui costruivano le loro marmoree dimore i ricchi di prima della Rivoluzione, i proprietari delle piantagioni di canna da zucchero della vicina e splendida <strong>Valle&nbsp; de los</strong> <strong>Ingenios.</strong></p>\r\n<p><strong>Plaza Mayor</strong>, al centro della Zona Monumento. E&acute;la zona coloniale tutta da visitare, con Musei importanti, dimore antiche molto ben conservate, strade di pietra, bar e ristoranti, frequentatissima da stranieri.</p>\r\n<p>&nbsp;</p>\r\n<p>Alcuni Musei: <strong>Museo de la Historia, Casa de la Cultura, Museo de la</strong> <strong>Arqueolog&iacute;a</strong>, con oggetti delle popolazioni precolombiane, <strong>Museo Romantico,</strong> <strong>Museo Nacional de la Lucha contra Bandi</strong><strong>dos</strong>, storia della lotta ai contro-rivoluzionari.</p>\r\n<p>&nbsp;</p>\r\n<p align="center"><strong>&nbsp;</strong></p>\r\n<h2 align="center"><strong>Spiagge</strong></h2>\r\n<p><strong>&nbsp;</strong></p>\r\n<p><strong>&nbsp;</strong></p>\r\n<p><strong>Playa Anc&oacute;n</strong>, a 12 kilometri dalla Citt&aacute;, bella spiaggia di sabbia bianca e pulita.</p>\r\n<p>Si raggiunge con l&rsquo;autobus turistico, prezzo 4 CUC a persona andata e ritorno, attenti agli orari, o con il Coco-taxi, prezzo 12 CUC andata e ritorno, due persone al massimo. In quest&rsquo;ultimo caso, mettetevi d&rsquo;accordo con il conducente per l&rsquo;ora del ritorno e pagate solo quando vi riporter&aacute; in Citt&aacute;. Caffetteria e ristorante.</p>\r\n<p><strong>Playa La Boca</strong>, bel posto ma acqua contaminata, da evitare. Per di pi&uacute;, ogni tanto qualche pescecane entra nella baia per risalire il vicino fiume.</p>\r\n<p><strong>Playa Maria Aguilar</strong>. Pi&uacute; piccola di Anc&oacute;n, per&oacute; piacevole e pulita anche se pietrosa. Con un buon ristorante, di Stato, ma in questo caso di buon livello e non caro.</p>\r\n<p>&nbsp;</p>\r\n<p><strong>&nbsp;</strong></p>\r\n<h2 align="center"><strong>Da vedere</strong></h2>\r\n<p><strong>&nbsp;</strong></p>\r\n<p><strong>&nbsp;</strong></p>\r\n<p><strong>Tope de Collantes</strong>. Zona montagnosa, circa 700 metri di altezza, aria fresca, pinete, veramente bella e rilassante. La strada per arrivarci per&oacute; non &eacute; facile, impervia e piena di buche.</p>\r\n<p><strong>Valle de Los Ingenios</strong><strong>.</strong> Ampia valle appena fuori Citt&aacute;, antica&nbsp; zona di piantagioni di zucchero, dove lavoravano migliaia di schiavi. Rimangono esempi di architettura industriale e di dimore ben conservati.</p>\r\n<p>Dal Mirador (Osservatorio) appena fuori Citt&aacute; sulla strada per Sancti Spiritus, si ha una vista quasi completa di questa spettacolare valle. C&rsquo;&eacute; anche un bar.</p>\r\n<p>&nbsp;</p>\r\n<p><strong>&nbsp;</strong></p>\r\n<p><strong>&nbsp;</strong></p>\r\n<p><strong>Canchanchara.</strong> Bar della Zona Monumento, aperto dalle 9 alle 21. La bevanda dello steso nome, buonissima, &eacute; a base di acqua, limone, miele e aguardiente. Nel pomeriggio c&rsquo;&eacute; anche musica.</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p align="center"><strong>&nbsp;</strong></p>\r\n<h2 align="center"><strong>Divertirsi di notte</strong></h2>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>Non metto gli indirizzi, a Trinidad nessuno li usa, bisogna chiedere.&nbsp;Meglio informarsi con i proprietari delle &ldquo; Casas&rdquo;.</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Escalinata, Casa de la M&uacute;sica e Piano bar, tutti lass&uacute; riuniti .</strong></p>\r\n<p><strong>Escalinata.</strong> Dalle 21 all&rsquo;una, all&rsquo;aria aperta, nella Zona Monumento.</p>\r\n<p>Il meglio della notte trinitaria, magnifico luogo d&rsquo;incontro fra cubani e stranieri di ogni dove, fiume in piena d&rsquo;umanit&aacute; che si diverte, che si muove senza sosta in una scenografia da teatro, ineguagliabile.</p>\r\n<p>La gente si siede ai tavolini, sui muretti, per terra, dove capita. Si balla e si beve fino all&rsquo;una di notte. Ci sono anche spettacoli di danza.</p>\r\n<p>Non esiste un posto cos&iacute; in tutta Cuba.</p>\r\n<p>Non si paga l&rsquo;entrata, &eacute; all&rsquo;aperto! A meno che non ci sia in programma qualche concerto speciale, ma &eacute; raro.</p>\r\n<p>Per favore non perdetevi quest&rsquo;occasione.</p>\r\n<p>(Funziona anche di pomeriggio, sempre con musica ma tutto calmo, per rilassarsi)</p>\r\n<p>Dopo la Escalinata, si va alla <strong>Casa de la M&uacute;sica</strong>, l&iacute; in cima. Funziona dall&rsquo;una alle 4. Si balla, buoni gruppi musicali. Poi, per quelli che non vogliono proprio andarsene,&nbsp; c&rsquo;&eacute; sempre il <strong>Piano Bar.</strong></p>\r\n<p>&nbsp;</p>\r\n<p><strong>Casa de la Trova</strong>, gruppi di musica tradizionale. Molto bello, si pu&oacute; ballare.</p>\r\n<p><strong>Palenque</strong><strong>.</strong> Musica e balli di origine africana e haitiana, anche di pomeriggio.</p>\r\n<p>Piacevolissimo, e anche qui si pu&oacute; ballare.</p>\r\n<p><strong>Ruinas del Teatro Brunet</strong>. Musica tradizionale, afro-cubana. Corsi di ballo nel pomeriggio, chiedete al barman.</p>\r\n<p><strong>Casa Artex</strong>. Musica tradizionale, spettacoli cubani.</p>\r\n<p><strong>Discoteca dell&rsquo;Hotel Las Cuevas</strong>. Dentro un&rsquo;ampia e alta grotta. Da marted&iacute; a domenica.</p>\r\n<p>&nbsp;</p>\r\n<h2 style="text-align: center;"><strong>&nbsp;</strong></h2>\r\n<h2 style="text-align: center;"><strong>Suggerimenti</strong></h2>\r\n<p><strong>&nbsp;</strong></p>\r\n<p><strong>&nbsp;</strong></p>\r\n<p>Se vuoi sapere di pi&uacute; di Trinidad, mettiti in contatto con noi, clicca qui</p>\r\n<p>Se invece vuoi conoscere le nostre offerte, clicca qui</p>', '<p>Cr&eacute;anos, en realidad, el tiempo es curvo en la Trinidad colonial, se mueve sin descanso pero en circulo, eternamente obliga a la Ciudad a pasar y volver a pasar siempre en los mismos puntos, llevandose en los hombros gente y calles empedradas, casas antiguas y patios tranquilos, y llena de m&uacute;sica y baile por la noche.</p>\r\n<p>Esplendida siempre, y para ustedes.</p>\r\n<p>Trinidad funciona como un im&aacute;n, joya brillante que atrae constantemente la atenci&oacute;n de todos, merecida.</p>\r\n<p>Sientanse por la tarde en el bar de la Escalinata, cuando empieza a hacer un poco menos de calor y la luz se pone color de rosa y de color de rosa y amarillo y azul se pintan casas y plazas, y miren hacia abajo, hacia la Ciudad, hasta la playa. Pu&eacute;s escr&iacute;banos y cu&eacute;ntanos todo.</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<h2>Diversi&oacute;n en la noche</h2>\r\n<p>No es mucho, sin embargo esto es lo que uno puede decir.</p>\r\n<p>Cabaret Bimbo, Parque Granma.</p>\r\n<p>La Esquina, es una cosa extra&ntilde;a, uno entra y piensa que tiene la direcci&oacute;n equivocada. Pero entonces la gente viene y luego se vuelve.</p>\r\n<p>&nbsp;Cuco bar, un mont&oacute;n de gente.</p>\r\n<p>&nbsp;A Bayamo en coche, relajado y amistoso.</p>\r\n<p>&nbsp;La Bayamesa, discoteca del hotel Sierra Maestra.</p>\r\n<p>&nbsp;La Barbacoa, cabaret Hotel El Yarey.</p>\r\n<p>&nbsp;Royalton Hotel Caf&eacute;.</p>\r\n<h2 align="center"><strong>Informaciones sobre la Ciudad, y algunos lugares para visitar</strong></h2>\r\n<p align="center">&nbsp;</p>\r\n<p><strong>&nbsp;</strong></p>\r\n<p>Fundada por Diego Vel&aacute;squez en al a&ntilde;o 1515, con el nombre de Villa de la Sant&iacute;sima Trinidad.</p>\r\n<p>Patrimonio de la Humanidad seg&uacute;n la Unesco.</p>\r\n<p>Setenta mil habitantes, y en continua expansi&oacute;n. Llega gente desde todo el Pa&iacute;s, desde Sancti Spiritus, Cienfuegos, La Habana, y Oriente de la Isla.</p>\r\n<p>Trinidad es Ciudad-Museo en estilo colonial, entre el mar y el grupo monta&ntilde;oso del Escambray, muy bien conservada.</p>\r\n<p>Aqu&iacute; constru&iacute;an sus mansiones los ricos antes de la Revoluci&oacute;n, los due&ntilde;os&nbsp; de las plantaciones de ca&ntilde;a de az&uacute;car, en la cercana y lind&iacute;sima <strong>Valle de los Ingenios.</strong></p>\r\n<p>&nbsp;</p>\r\n<p><strong>Plaza Mayor</strong>, al centro de la Zona Monumento. Esla zona colonial, con Museos importantes, mansiones antiguas muy bien conservadas, calles de piedra, con bares y restaurantes, muy frecuentadas por extranjeros.</p>\r\n<p>&nbsp;</p>\r\n<p>Algunos Museos:&nbsp;<strong>Museo de la Historia, Casa de la Cultura, Museo de la</strong>&nbsp;<strong>Arqueolog&iacute;a</strong>, <strong>Museo Romantico,</strong>&nbsp;<strong>Museo Nacional de la Lucha contra Bandidos</strong>.</p>\r\n<p>&nbsp;</p>\r\n<p align="center"><strong>&nbsp;</strong></p>\r\n<h2 align="center"><strong>Playas</strong></h2>\r\n<p><strong>&nbsp;</strong></p>\r\n<p><strong>&nbsp;</strong></p>\r\n<p><strong>Playa Anc&oacute;n</strong>, a 12 kil&oacute;metros de la Ciudad, arena blanca y limpia.</p>\r\n<p>Se llega all&iacute; con el autob&uacute;s tur&iacute;stico, precio&nbsp; 4 CUC por persona ida y vuelta cuidado con los horarios. Con el Coco-taxi, precio 12 CUC ida y vuelta, dos personas como m&aacute;ximo. Se pongan de acuerdo con el chofer por la vuelta, y paguen s&oacute;lo cuando los llevar&aacute; de regreso a la Ciudad. Cafeter&iacute;a y restaurante.</p>\r\n<p><strong>Playa La Boca</strong>, lugar lindo pero agua contaminada. Adem&aacute;s, de vez en cuando, hay tiburones que entran a la bah&iacute;a para subir el r&iacute;o cercano.</p>\r\n<p><strong>Playa Maria Aguilar</strong>. M&aacute;s peque&ntilde;a que Anc&oacute;n, agradable y limpia pero piedrosa. &nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><strong>&nbsp;</strong></p>\r\n<h2 align="center"><strong>Para ver</strong></h2>\r\n<p><strong>&nbsp;</strong></p>\r\n<p><strong>&nbsp;</strong></p>\r\n<p><strong>Tope de Collantes</strong>. Zona monta&ntilde;osa, 700 metros de altura, aire fresco, pinos, linda y relajante, pero la carretera&nbsp; para llegar all&iacute; no es f&aacute;cil.</p>\r\n<p><strong>Valle de Los Ingenios.</strong>&nbsp;Gran valle cercana a la Ciudad, antigua zona de plantaciones de ca&ntilde;a de azucar, donde trabajaban miles y miles de esclavos. Quedan ejemplos de arquitectura industrial y de mansiones bien conservadas. Desde el Mirador se puede observar esta espectacular valle. Tambi&eacute;n hay una cafeter&iacute;a.</p>\r\n<p><strong>Canchanchara.</strong>&nbsp;Bar de la Zona Monumento, abierto desde las 9 hasta las 9 p.m. Sirven una bebida del mismo nombre, muy sabrosa y fresca, en recipientes de barro, con base de lim&oacute;n, miel y aguardiente. Por la tarde hay m&uacute;sica en vivo.</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p align="center"><strong>&nbsp;</strong></p>\r\n<h2 align="center"><strong>Diversiones por las noches</strong></h2>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>No vamos a poner las direcciones, en Trinidad nadie los usa, hay que preguntar. Mejor con los due&ntilde;os de las Casas.</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Escalinata, Casa de la M&uacute;sica e Piano bar, todos juntos all&iacute; arriba.</strong></p>\r\n<p><strong>Escalinata.</strong>&nbsp;Desde las 9 p.m. a las cuatro de la madrugada, al aire libre, en la Zona Monumento.</p>\r\n<p>Lo mejor de la noche trinitaria, magnifico lugar de encuentro entre cubanos y extranjeros de todo el mundo, r&iacute;o en su m&aacute;ximo caudal de humanidad que se divierte, que se mueve sin parar en una escenograf&iacute;a de teatro, impresionante.&nbsp;</p>\r\n<p>La gente se sienta a las mesitas, en los escalones, donde sea. Se baila y se toma hasta la madrugada. Tambi&eacute;n hay espect&aacute;culos de danza.</p>\r\n<p>No hay un lugar parecido en toda Cuba. Adem&aacute;s la entrada es gratis, porqu&eacute; es al aire libre.</p>\r\n<p>Por favor, no se pierdan esta ocasi&oacute;n !</p>\r\n<p>Funciona tambi&eacute;n por la tarde, siempre con m&uacute;sica pero calmadito, para relajarse.</p>\r\n<p>Despu&eacute;s de la Escalinata, la gente se va a la <strong>Casa de la M&uacute;sica</strong>, all&iacute; arriba. Funciona desde la una a la cuatro de la madrugada. Al final, por los que no quieren irse para la Casa, siempre hay el <strong>Piano Bar.</strong></p>\r\n<p><strong>Casa de la Trova</strong>, grupos de m&uacute;sica tradicional. Muy lindo, se puede bailar.</p>\r\n<p><strong>Palenque.</strong>&nbsp;M&uacute;sica y bailes di origen africano e haitiano, anche por la tarde.</p>\r\n<p><strong>Ruinas del Teatro Brunet</strong>. M&uacute;sica tradicional afro-cubana. Clase de baile por la tarde, pregunten al cantinero.</p>\r\n<p><strong>Casa Artex</strong>. M&uacute;sica tradicional, espect&aacute;culos cubanos.</p>\r\n<p><strong>Discoteca dell&rsquo;Hotel Las Cuevas</strong>. En una amplia y alta cueva. Desde martes a domingo.</p>\r\n<p>&nbsp;</p>\r\n<p align="center"><strong>&nbsp;</strong></p>\r\n<p>&nbsp;</p>\r\n<h2 align="center"><strong>Sugerencias</strong></h2>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>Si quieres saber m&aacute;s sobre Trinidad, ponte en contacto con nosotros, <span style="text-decoration: underline;">dale clic aqu&iacute;</span></p>\r\n<p>Si quieres conocer nuestras ofertas, <span style="text-decoration: underline;">dale clic aqu&iacute;</span></p>', 1, 0, 8, '2012-10-24 18:29:46'),
(2, 'Cuba', 'Santiago de Cuba', '2_santiago-de-cuba', '<p>La pi&uacute; africana delle Citt&aacute; di Cuba</p>', '<p>La m&aacute;s africana de las Ciudades de Cuba</p>', '<p>La pi&uacute; africana delle Citt&aacute; di Cuba, come tutto l&rsquo;Oriente del Paese invaso dai latifondisti&nbsp; in fuga dalle ribellioni di Haiti, con al seguito i loro schiavi.</p>\r\n<p>Citt&aacute; con un ruolo assolutamente preponderante nelle guerre per l&rsquo;indipendenza dalla Spagna, nella resistenza contro Batista e nella Rivoluzione.</p>\r\n<p>Citt&aacute; fra le pi&uacute; belle di Cuba, abitata da gente gioiosamente musicale, e ve ne renderete conto quando visiterete i luoghi di musica e ballo, patria del <em>son </em>e della <em>conga</em>, nati dalla fusione di folklore musicale spagnolo e di straordinari, entusiasmanti ritmi africani, anche questo patrimonio culturale degli schiavi.</p>\r\n<p>In questi turbini mi perdo, e devo davvero dire che questi africani ne sapevano qualcosa di musica (e non solo di musica, <em>c&rsquo;est pas vrai</em> <em>monsieur Picasso?</em>)</p>\r\n<p>&nbsp;</p>\r\n<h2 align="center">Informazioni sulla Citt&aacute; e posti da vedere.</h2>\r\n<h2 align="center"><strong>&nbsp;</strong></h2>\r\n<p>&nbsp;</p>\r\n<p>Fondata nel 1515.</p>\r\n<p>Abitanti, 430.000.</p>\r\n<p>&nbsp;</p>\r\n<p>Centro Citt&aacute; con quattro piazze splendide, <strong>Parque Ajedrez, Plaza Dolores, Plaza</strong> <strong>de Marte e Parque C&eacute;spedes</strong>, con la casa dell&rsquo;ormai per voi famoso fondatore di Citt&aacute; Diego Velasquez, e la <strong>Sierra Maestra</strong> come sfondo, il pi&uacute; importante sistema montagnoso di Cuba.</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Fortezza del Morro</strong>, notevole esempio di&nbsp; architettura militare.</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Basilica de la Virgen de la Caridad del Cobre.</strong> Splendida l&rsquo;immagine della Virgen dentro la Basilica, circondata da marmi. &Eacute; uno dei siti culturalmente pi&uacute; importanti per il popolo cubano. Nel sincretismo religioso cos&iacute; diffuso a Cuba, la Virgen &eacute; associata a Och&uacute;n (vedi documento &ldquo;Religioni&rdquo;)</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Riserva Ecologica Siboney,</strong> a 15 km dalla Citt&aacute;. Flora da proteggere perch&eacute; i suoli sono troppo superficiali e le temperature alte. Grotte, rettili e uccelli di molte specie. A Cuba non esistono rettili velenosi.</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Sierra de la Gran Piedra</strong>. Sistema montagnoso di pi&uacute; di mille metri d&rsquo;altezza, famoso per la presenza di un enorme blocco di roccia del peso di quasi 70 mila tonnellate.</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Museo de Ciencias Naturales Tomas Romay,</strong> archeologia, oceanologia, planetario.</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Museo Bacardi,</strong> Calle Pio Rosado e Aguilera, arti plastiche, architettura&nbsp; e storia.</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Museo del Carnaval</strong>, Calle Heredia 301.</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Museo del Ron</strong>, Calle San Basilio 358.</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p align="center"><strong>&nbsp;</strong></p>\r\n<h2 align="center">Divertirsi di notte</h2>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>Molta scelta per i divertimenti notturni, mi limiter&oacute; a segnalarvene alcuni, dove musica, danza e partecipazione del pubblico sono le caratteristiche di base, nello stile della Citt&aacute;.</p>\r\n<p>Non perdetevene neppure uno.</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Caf&eacute; Cantante Niagara</strong>, Teatro Heredia, Avenida Americas, son e boleros, atmosfera trascinante, tipicamente santiaguera.</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Club 300</strong><strong>,</strong> Calle Aguilera, boleros e bailadas.</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Casa de la M&uacute;sica</strong>, Calle Corona y Enramado, gruppi musicali.</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Casa de la Trova</strong><strong>,</strong> Calle Heredia 208, son e boleros, la pura trova di Santiago, i migliori gruppi di son, autentico monumento alla musica cubana.</p>\r\n<p>Chi non ci va sar&aacute; punito dagli dei.</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Casa de las Tradiciones Populares</strong>, La Casona, &ldquo;barrio&rdquo;(quartiere) Tivoli, son, boleros, poesia, arti e fotografia.</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Casa del Caribe,</strong> Calle 13 n. 154, musica afro cubana, haitiana, e tradizionale latino-americana, consultare il programma.</p>\r\n<p>&nbsp;</p>\r\n<p><strong>La Jut&iacute;a Conga</strong>, sede dell&rsquo;UNEAC, Unione degli scrittori e artisti di Cuba, Calle Heredia, gruppi musicali e solisti, galleria d&rsquo;arte con bar e qualcosa da mangiare, frequentato da pittori, poeti e scrittori.</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Patio di Artex</strong><strong>,</strong> Calle Heredia 304, musica tradizionale.</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Patio de Los Dos Abuelos</strong>, all&rsquo;aria aperta, Calle P&eacute;rez Carb&oacute; 5, son e boleros, gruppi musicali, poesia, spettacoli. Salsa, merengue e rock latino nei fine settimana, consultare il programma.</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Discoteca Karaoke Club La Iris,</strong> Calle Aguilera 617, salsa, merengue e rock latino.</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Discoteca Club Tropical,</strong> Autostrada Nazionale km. 1</p>\r\n<p>&nbsp;</p>\r\n<p><strong>La Maison,</strong> Avenida Manduley 52, spettacoli, negozi, ristorante.</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Bar La Columnita</strong>, Calle San Felix,punto d&rsquo;incontro dei nottambuli, piacevole, ci vanno tutti.</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Pico Real,</strong> Avenida de Las Americas y M, terrazza dell&rsquo;ultimo piano dell&rsquo;Hotel Melia. Fantastica la vista sulla Citt&aacute;, musica dal vivo.</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Taberna de Los Dolores</strong>, Calle Aguilera y Plaza Dolores, musica dal vivo, molto economico e in &ldquo;pesitos&rdquo; cubani, vedi documento &rdquo;Monete&rdquo;.</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Cabaret San Pedro del Mar</strong>, Carretera Morro km 8, musiche romantiche, spettacoli, ottime bevande. Bello.</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<h2 style="text-align: center;">Suggerimenti</h2>\r\n<p><strong>&nbsp;</strong></p>\r\n<p><strong>&nbsp;</strong></p>\r\n<p><strong><br /></strong>Se vuoi sapere di pi&uacute; su Santiago de Cuba, mettiti in contatto con noi, clicca qui</p>\r\n<p>&nbsp;</p>\r\n<p>Se invece vuoi conoscere le nostre offerte, clicca qui</p>\r\n<p>&nbsp;</p>', '<p>La m&aacute;s africana de las Ciudades de Cuba, al igual que todo el Oriente del Pa&iacute;s, invadido en pasado por los terratenientes que se hu&iacute;an de las rebeliones en Hait&iacute;, con sus esclavos a remolque.</p>\r\n<p>Ciudad con un papel absolutamente dominante en las guerras por la independencia, en la resistencia contra Batista y en la Revoluci&oacute;n.</p>\r\n<p>Ciudad de las m&aacute;s bellas de Cuba, habitada por gente alegramente m&uacute;sical, y ustedes se dar&aacute;n cuenta al visitar los lugares de la m&uacute;sica y de la danza. Lugar de nacimiento del "son" y de la "conga", nacidos de la fusi&oacute;n de la m&uacute;sica tradicional espa&ntilde;ola y de extraordinarios, emocionantes ritmos Africanos, patrimonio cultural de los esclavos.</p>\r\n<p>Se puede perderse en estos torbellinos, y realmente hay que decir que los africanos sab&iacute;an algo de m&uacute;sica (y no s&oacute;lo m&uacute;sica, &iquest;&nbsp;<em>c''est pas vrai monsieur</em>&nbsp;<em>Picasso&nbsp;?</em>)</p>\r\n<p align="center">&nbsp;</p>\r\n<h2 align="center">Informaciones sobre la Ciudad y lugares&nbsp; para visitar.</h2>\r\n<p align="center"><strong>&nbsp;</strong></p>\r\n<p>&nbsp;</p>\r\n<p>Fundada en al a&ntilde;o 1515.</p>\r\n<p>Habitantes&nbsp; 430.000</p>\r\n<p>&nbsp;</p>\r\n<p>Centro Ciudad con cuatro esplendidas plazas, <strong>Parque Ajedrez, Plaza Dolores, Plaza</strong>&nbsp;<strong>de Marte e Parque C&eacute;spedes</strong>, y la&nbsp;<strong>Sierra Maestra</strong>&nbsp;como escenario, el m&aacute;s sistema de monta&ntilde;as de Cuba.</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Fortaleza del Morro</strong>, ejemplo de arquitectura militar.</p>\r\n<p><strong>Bas&iacute;lica de la Virgen de la Caridad del Cobre.</strong>&nbsp;Esplendida la imagen de la &nbsp;Virgen dentro la Bas&iacute;lica. Es uno de los sitios culturalmente m&aacute;s importantes para el pueblo cubano.</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Reserva Ecol&oacute;gica Siboney,</strong>&nbsp;a 15 km afuera de la Ciudad. Flora por proteger porque el suelo es demasiado superficial &nbsp;y las temperaturas demasiado altas. Cuevas, r&eacute;ptiles y aves de muchas especies. A Cuba no hay r&eacute;ptiles venenosos.</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Sierra de la Gran Piedra</strong>. Sistema monta&ntilde;oso di m&aacute;s de mil metros de altura.</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Museo de Ciencias Naturales Tomas Romay,</strong>&nbsp;arqueolog&iacute;a, oceanolog&iacute;a, planetario.</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Museo Bacardi,</strong>&nbsp;Calle Pio Rosado e Aguilera, artes pl&aacute;sticas, arquitectura&nbsp; y historia.</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Museo del Carnaval</strong>, Calle Heredia 301.</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Museo del Ron</strong>, Calle San Basilio 358.</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p align="center"><strong>&nbsp;</strong></p>\r\n<h2 align="center">Diversiones por las noches</h2>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Caf&eacute; Cantante Niagara</strong>, Teatro Heredia, Avenida Am&eacute;ricas, son e boleros, t&iacute;pica atm&oacute;sfera santiaguera.</p>\r\n<p><strong>Club 300,</strong>&nbsp;Calle Aguilera, boleros e bailadas.</p>\r\n<p><strong>Casa de la M&uacute;sica</strong>, Calle Corona y Enramado, grupos musicales.</p>\r\n<p><strong>Casa de la Trova,</strong>&nbsp;Calle Heredia 208, son e boleros, la verdadera trova de Santiago, los mejores grupos de son, aut&eacute;ntico monumentos de la m&uacute;sica cubana.</p>\r\n<p><strong>Casa de las Tradiciones Populares</strong>, La Casona, barrio Tivoli, son, boleros, poemas, artes y fotograf&iacute;a.</p>\r\n<p><strong>Casa del Caribe,</strong>&nbsp;Calle 13 n. 154, m&uacute;sica afro-cubana, haitiana, y tradicional latino-americana.</p>\r\n<p><strong>La Jut&iacute;a Conga</strong>, sede del UNEAC, Uni&oacute;n de los escritores y artistas de Cuba, Calle Heredia, grupos musicales e solistas, galer&iacute;a de arte, con bar e algo para comer, frecuentado por pintores, poetas y escritores</p>\r\n<p><strong>Patio de Artex,</strong>&nbsp;Calle Heredia 304, m&uacute;sica tradicional.</p>\r\n<p><strong>Patio de Los Dos Abuelos</strong>, al aire libre, Calle P&eacute;rez Carb&oacute; 5, son e boleros, grupos musicales, poes&iacute;a, espect&aacute;culos. Salsa, merengue e rock latino en los fines de semana.</p>\r\n<p><strong>Discoteca Karaoke Club La Iris,</strong>&nbsp;Calle Aguilera 617, salsa, merengue y rock latino.</p>\r\n<p>&nbsp;<strong>Discoteca Club Tropical,</strong>&nbsp;Autopista Nacional, al km. 1</p>\r\n<p>&nbsp;<strong>La Maison,</strong>&nbsp;Avenida Manduley 52, espect&aacute;culos, tiendas y restaurantes.</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Bar La Columnita</strong>, Calle San F&eacute;lix, lugar de encuentro. Agradable para transnochar.</p>\r\n<p><strong>Pico Real,</strong>&nbsp;Avenida de Las Am&eacute;ricas y M, terraza al &uacute;ltimo piso del Hotel Meli&aacute;. Fant&aacute;stica la vista de la Ciudad, m&uacute;sica en vivo.</p>\r\n<p><strong>Taberna de Los Dolores</strong>, Calle Aguilera y Plaza Dolores, m&uacute;sica en vivo, muy barato y en &ldquo;pesitos&rdquo; nacionales cubanos.</p>\r\n<p><strong>Cabaret San Pedro del Mar</strong>, Carretera Morro km 8, m&uacute;sicas rom&aacute;nticas, espect&aacute;culos, buenas bebidas. Lindo.</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<h2 align="center">Sugerencias</h2>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>Si quieres saber m&aacute;s sobre Santiago, ponte en contacto con nosotros, <span style="text-decoration: underline;">dale clic aqu&iacute;</span></p>\r\n<p>Si quieres conocer nuestras ofertas, <span style="text-decoration: underline;">dale clic aqu&iacute;</span></p>', 1, 0, 7, '2012-10-24 18:46:59'),
(3, 'Cuba', 'La Havana ', '3_la-havana', '', '', '<p>Non aver paura di niente e di nessuno, e guarda diritto in faccia una della Citt&aacute; pi&uacute; stupefacenti del mondo.</p>\r\n<p>Non aver paura di niente e di nessuno, ma tieni ben aperti occhi e orecchie, ascolta, osserva e non ti perdere nulla.</p>\r\n<p>All&rsquo;Havana non avrai nemici, ma da dietro l&rsquo;angolo pu&oacute; venir fuori l&rsquo;inaspettato ,&nbsp;con la faccia insieme del riso o del pianto, del comprensibile o del misterioso, avvinghiati indissolubilmente. Hai gi&aacute; capito, La Havana &eacute; tutto e il suo contrario.</p>\r\n<p>Citt&aacute; entusiasmante, piena di musica, di balli e di danze, di gente curiosa e apertissima, piena di Musei, Cinema&nbsp; e Teatri, di Arte e di Storia. Non si conosce il mondo, se non s&rsquo;&eacute; visitata La Havana.</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<h2 align="center">Informazioni sulla Citt&aacute;, e solo alcuni dei luoghi che mi sembra importante conoscere</h2>\r\n<p align="center"><strong>&nbsp;</strong></p>\r\n<p style="text-align: justify;" align="center">Fondata nel 1519 da Diego Velasquez de Cuellar, con il nome di Villa de San Cristobal de La Habana.</p>\r\n<p>Patrimonio dell&rsquo;Umanit&aacute;, secondo l&rsquo;Unesco.</p>\r\n<p>Numero di abitanti,&nbsp; 2.140.000, in continuo aumento.</p>\r\n<p align="center"><strong>&nbsp;</strong></p>\r\n<p align="center"><strong>&nbsp;</strong></p>\r\n<p><strong>Habana Vieja,</strong><span style="color: #000000;"> il centro storico Patrimonio Unesco dell&rsquo;Umanit&aacute;.</span></p>\r\n<p><strong>Plaza de Armas, Parque Central y Plaza de la Catedral</strong> splendide architetture coloniali e in stile barocco.</p>\r\n<p><strong>Barrio Chino</strong>, quartiere cinese denso di vita, bar e ristoranti. Bello.</p>\r\n<p><strong>Bodeguita del Medio e Floridita</strong>, bar frequentatissimi da visitatori di tutto il mondo, cos&iacute; come, di sera, il piano bar dell&rsquo;Hotel <strong>Ambos Mundos</strong>, conosciuti come i rifugi dove Ernest Hemingway passava ore a mandar gi&uacute; &ldquo;daiquir&iacute;&rdquo;.</p>\r\n<p><strong>Castillo de la Real Fuerza e Castillo de los Tres Reyes del Morro,</strong> antiche fortezze a difesa della Citt&aacute;, al tempo dei pirati.</p>\r\n<p><strong>Universidad de La Havana e Plaza de la Revoluci&oacute;n.</strong></p>\r\n<p><strong>Jard&iacute;n Zoologico,</strong> animali con qualche problema, come in tutti gli zoo.</p>\r\n<p><strong>Casa de Las Americas</strong>, Calle 3&nbsp; y G, Vedado, spazio di dibattiti, scrittori e giovani artisti cubani.</p>\r\n<p><strong>Capitolio de La Havana,</strong> simbolo della Citt&aacute;, aperto al pubblico.</p>\r\n<p><strong>Malec&oacute;n,</strong> il lungomare, per incontrarsi e conversare. C&rsquo;&eacute; sempre gente, di notte e di giorno.</p>\r\n<p><strong>Puerto de La Havana,</strong> porto antico e suggestivo.</p>\r\n<p><strong>Parque Lenin,</strong> parco immenso di 760 ettari, divertimento e ristoranti. Molti bambini e scolaresche.</p>\r\n<table style="width: 31%;" border="0" cellpadding="0">\r\n<tbody>\r\n<tr>\r\n<td valign="top">&nbsp;</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p><strong>Marina Hemingway</strong>, a Santa F&eacute;, a occidente della Citt&aacute;, bellissimo centro nautico, tranquillo e suggestivo (Quinta Avenida e poi via, sempre diritto, poi chiedete)</p>\r\n<p align="center"><strong>&nbsp;</strong></p>\r\n<p align="center"><strong>&nbsp;</strong></p>\r\n<h2 align="center">Alcuni dei molti Musei</h2>\r\n<p align="center"><strong>&nbsp;</strong></p>\r\n<p>&nbsp;</p>\r\n<p><strong>Museo Nacional de Bellas Artes,</strong> 9-17, chiuso luned&iacute; e marted&iacute;,&nbsp;Calle Trocadero, entre Agramonte e Av. Las Misiones</p>\r\n<p><strong>Museo de la Ciudad de La Habana,</strong> da marted&iacute; a sabato, 11.30-17,&nbsp;domenica&nbsp; 9-12, Calle Tac&oacute;n, entre Obispo y O&rsquo;Reilly</p>\r\n<p><strong>Museo de Arte Colonial</strong>, da marted&iacute; a sabato 9-17, domenica 9-13,&nbsp;Calle San Ignacio n. 61, Plaza Cat&eacute;dral</p>\r\n<p><strong>Museo de Historia Natural</strong>, da marted&iacute; a sabato, 10.15-17.45,&nbsp;domenica 9.15-12.45, Paseo del Prado entre Dragones y San Mart&iacute;n</p>\r\n<p><strong>Museo de la Revoluci&oacute;n</strong>, da marted&iacute; a sabato, 13-18, domenica 9-13,&nbsp;Calle Refugio n.1, entre Monserrate y Zuluela</p>\r\n<p><strong>Museo de Artes Decorativas</strong>, da marted&iacute; a sabato, 11-18.30,&nbsp;domenica 9-13, Calle 17 n. 502, entre D&nbsp; y&nbsp; E, Vedado</p>\r\n<p><strong>Museo Napoleonico</strong>, da marted&iacute; a venerd&iacute;, 9-12/13-16, a&nbsp;sabati alternati 9-12/13-16, Calle San Miguel angolo Calle Ronda, Vedado</p>\r\n<p><strong>Museo Archivo de la M&uacute;sica</strong>, da marted&iacute; a sabado, 10-18, domenica 9-12,&nbsp;Calle Carcel n. 1, entre Aguilar y Habana</p>\r\n<p><strong>Museo &Aacute;rabe,</strong> da marted&iacute; a sabato, 14.30-18.30/19-21.45, domenica 9-13,&nbsp;Calle Oficios, entre Obispo y Obrapia</p>\r\n<p><strong>Casa del Africa,</strong> da marted&iacute; a sabato, 14.30-18.30, domenica 9-13,&nbsp;Calle Obrapia n. 157, entre Mercaderes y San Ignacio</p>\r\n<p><strong>Centro Wilfredo Lam</strong>, da luned&iacute; a venerd&iacute;, 8.30-16.30, bsabati alternati 8.30-16,30, Calle Oficio y Acosta</p>\r\n<p>&nbsp;</p>\r\n<p align="center">&nbsp;</p>\r\n<h2 align="center">Di notte, grande scelta</h2>\r\n<p>&nbsp;</p>\r\n<p>Non &eacute; facile elencare i posti di divertimento nortturno all&rsquo;Habana, ce ne sono dapppertutto, basta guardarsi attorno, e poi quasi ogni albergo ha il suo proprio spettacolo. Comunque, ecco alcuni suggerimenti.</p>\r\n<p><strong>&nbsp;</strong></p>\r\n<p><strong>Salon Rosado de la Tropical, Benny Mor&eacute;,</strong>&nbsp;scarsamente indicato sulle guide. Musica e balli demenziali in un mondo straordinario, disposto su tre livelli che noi denominiamo Inferno,&nbsp; Purgatorio e &nbsp;Paradiso, quello in alto, il Paradiso ovviamente, riservato ai pochi e avventurosi stranieri .</p>\r\n<p>Naturalmente, noi preferiamo l&rsquo;Inferno del piano terra, fra la perduta gente. Entriamo dal Paradiso, e immediatamente voliamo gi&uacute; all&rsquo;Inferno, &nbsp;abbandonandoci al godimento di demoniaci balli sfrenati sotto il palco, con la combriccola di turno che canta e urla e che frastorna di musica e ritmo.</p>\r\n<p>Moltissima gente per il fine settimana, quasi tutti cubani.&nbsp;Restaurato recentemente.</p>\r\n<p>Se&ntilde;oras y Se&ntilde;ores,&nbsp; siete avvisati, questo &eacute; il Popolo, questa &eacute; vera Cuba.&nbsp;Il Popolo va bene, per&oacute; metteteci un bel po&rsquo; di prudenza, la folla a volte &eacute; davvero troppa, e l&iacute; c&rsquo;&eacute; davvero di tutto.</p>\r\n<p>Avenida 41, entre 45 y 44, Playa.</p>\r\n<p><strong>Habana Caf&eacute;,</strong> musica, Paseo, entre 1 y 3.</p>\r\n<p><strong>Casa de la M&uacute;sica</strong>, musica e balli, Avenida Italia, entre Concordia y Neptuno.</p>\r\n<p><strong>Cabaret Turquino</strong><strong>,</strong> ultimo piano dell&rsquo;Hotel Habana Libre, discoteca, se non piove aprono il tetto, Calle 23 y L.</p>\r\n<p><strong>Caf&eacute; Cantante</strong>, discoteca, entrata laterale del Teatro Nacional de Cuba, Paseo y Calle 39.</p>\r\n<p><strong>Jazz Caf&eacute;, </strong>Calle 1 y Paseo.</p>\r\n<p><strong>Cabaret Tropicana</strong>, classico spettacolo di cabaret habanero, caro, Calle 72 y 43, Marianao, ovest della Citt&aacute;. Il <strong>Tropicana </strong>sta dentro un parco bellissimo, si pu&oacute; andarci anche di pomeriggio, c&rsquo;&eacute; un bar ristorante.</p>\r\n<p>Piano Bar del<strong>&ldquo;1830&rdquo;,</strong> Malec&oacute;n e 22, torrione antico sul mare.</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<h2 align="center">Solo alcuni dei molti Teatri</h2>\r\n<p align="center"><strong>&nbsp;</strong></p>\r\n<p align="center"><strong>&nbsp;</strong></p>\r\n<p><strong>Gran Teatro de La Habana</strong>, Calle Prado, entre San Rafael y San Jos&eacute; famoso per il Balletto Nazionale di Cuba. Musica classica e moderna.</p>\r\n<p><strong>Teatro America,</strong> Galliano esquina Concordia, concerti.</p>\r\n<p><strong>Teatro Nacional de Cuba</strong>, Paseo y 39, musica popolare cubana, solisti e humour. Laboratorio di teatro, pi&uacute; Caf&eacute; Cantante pi&uacute; Piano Bar.</p>\r\n<p><strong>Teatro Mella, </strong>Linea n. 657, entre A y B, riviste musicali, recitals e teatro, spettacoli di artisti cubani e internazionali.</p>\r\n<p><strong>Teatro Amadeo Rold&aacute;n</strong>, Calzada, entre Calle D&nbsp; y&nbsp; E, orchestre sinfoniche e recitals.</p>\r\n<p><strong>Teatro Karl Marx,</strong> Avenida Primera, entre Calles 8&nbsp; y&nbsp; 10, 5500 posti, concerti e spettacoli di grandi gruppi cubani e internazionali.</p>\r\n<p><strong>Basilica Menor de San Francisco de As&iacute;s</strong>, sulla Piazza che porta lo stesso nome, musica da camera e concerti corali.</p>\r\n<p><strong>Teatro Bertold Brecht</strong>, Calle 13 y Primera, Vedado.</p>\r\n<p><strong>Teatro El S&oacute;tano</strong>, Calle K, entre Calles 25 y 27, Vedado, opere di teatro moderno.</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<h2 style="text-align: center;">Suggerimenti</h2>\r\n<h3><span><strong>&nbsp;</strong></span></h3>\r\n<p><span>Se vuoi sapere di pi&uacute; sull''Havana, mettiti in contatto direttamente con noi, clicca qui</span></p>\r\n<p><span>Se invece vuoi conoscere le nostre offerte, clicca qui</span></p>\r\n<h3><span><strong>&nbsp;</strong></span></h3>\r\n<p align="center"><strong>&nbsp;</strong></p>\r\n<p align="center"><strong>&nbsp;</strong></p>\r\n<p><strong>&nbsp;</strong></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>', '<p>No tengan miedo a nada ni a nadie, y miren con atenci&oacute;n a una de las Ciudades m&aacute;s estupefacientes del mundo.</p>\r\n<p>No tengan miedo a nada ni a nadie, pero mantengan ojos y &nbsp;o&iacute;dos bien abiertos, escuchen, observen y no se pierdan nada.</p>\r\n<p>A La Habana no tienen enemigos, pero detr&aacute;s de la esquina puede surgir lo inesperado, con&nbsp;su rostro riente o llorante, del perfectamente comprensible o del misterioso y inexplicable, entrelazados. &nbsp;La Habana es todo y su contrario.</p>\r\n<p>Ciudad entusiasmante, llena de m&uacute;sica, bailes y danzas, de colores. Pueblo curioso, muy atento, vivo y abierto. Ciudad repleta de lugares de diversi&oacute;n, de Museos, de Cines y Teatros, Arte y Historia. &iquest;Que saben del mundo, si no han visitado La Habana?</p>\r\n<p>&nbsp;</p>\r\n<h2 align="center">Informaciones sobre la Ciudad, y s&oacute;lo algunos de los tantos lugares para visitar</h2>\r\n<p align="center"><strong>&nbsp;</strong></p>\r\n<p>Fundada en el a&ntilde;o 1519 por Diego Vel&aacute;squez de Cuellar, con el nombre de Villa de San Crist&oacute;bal de La Habana.</p>\r\n<p>Patrimonio de la Humanidad, seg&uacute;n la Unesco.</p>\r\n<p>Habitantes,&nbsp; 2.140.000, en continuo aumento.</p>\r\n<p align="center"><strong>&nbsp;</strong></p>\r\n<p align="center"><strong>&nbsp;</strong></p>\r\n<p><strong>Habana Vieja,</strong>&nbsp;el centro hist&oacute;rico.</p>\r\n<p><strong>Plaza de Armas, Parque Central y Plaza de la Catedral,</strong>&nbsp;esplendidas arquitecturas coloniales en estilo barroco.</p>\r\n<p><strong>Barrio Chino</strong>, barrio chino, muy vivo, repleto de bares y restaurantes. Lindo.</p>\r\n<p><strong>Bodeguita del Medio e Floridita</strong>, bares muy frecuentados por visitantes de todo el mundo, igual que, por la noche, el piano bar del Hotel Ambos Mundos, conocidos como los refugios donde Ernest Hemingway se dedicaba a tragar &ldquo;daiquir&iacute;&rdquo;.</p>\r\n<p><strong>Castillo de la Real Fuerza e Castillo de los Tres Reyes del Morro,</strong>&nbsp;antiguas fortalezas a defensa de la Ciudad, a la &eacute;poca de los piratas.</p>\r\n<p><strong>Universidad de La Habana y Plaza de la Revoluci&oacute;n.</strong></p>\r\n<p><strong>Jard&iacute;n Zool&oacute;gico,</strong>&nbsp;animales con algunos problemas, como en todos los zool&oacute;gicos del mundo.</p>\r\n<p><strong>Casa de Las Am&eacute;ricas</strong>, Calle 3&nbsp; y G, Vedado, escritores y j&oacute;venes artistas cubanos.</p>\r\n<p><strong>Capitolio de La Habana,</strong>&nbsp;s&iacute;mbolo de la Ciudad, entrada permitida al p&uacute;blico.</p>\r\n<p><strong>Malec&oacute;n,</strong>&nbsp;para encontrarse y conversar. Siempre hay mucha gente, por la noche y por el d&iacute;a.</p>\r\n<p><strong>Puerto de La Habana,</strong>&nbsp;puerto antiguo y atractivo.</p>\r\n<p><strong>Parque Lenin,</strong>&nbsp;parque inmenso de 760 hect&aacute;reas, entretenimientos y restaurantes. Muchos ni&ntilde;os y escuelas en visita.</p>\r\n<table style="width: 87px;" border="1" cellspacing="0" cellpadding="0">\r\n<tbody>\r\n<tr>\r\n<td valign="top" width="87">&nbsp;</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p><strong>Marina Hemingway</strong>, a Santa Fe, al occidente de la Ciudad. Centro n&aacute;utico de primera grandeza, muy lindo, tranquilo y atractivo. (Quinta Avenida y despu&eacute;s siempre recto)</p>\r\n<p align="center"><strong>&nbsp;</strong><strong>&nbsp;</strong></p>\r\n<h2 align="center">Algunos de los museos</h2>\r\n<p align="center"><strong>&nbsp;</strong></p>\r\n<p><strong>Museo Nacional de Bellas Artes,</strong>&nbsp;9-17, &nbsp;Calle Trocadero, entre Agramonte e Av. Las Misiones, cerrado entre lunes y martes</p>\r\n<p><strong>Museo de la Ciudad de La Habana,</strong>&nbsp;desde martes a s&aacute;bado, 11-30-5p.m., domingo&nbsp; 9-12, Calle Tac&oacute;n, entre Obispo y O&rsquo;Reilly</p>\r\n<p><strong>Museo de Arte Colonial</strong>, desde martes&nbsp; a s&aacute;bado 9-17, domenica 9-1 p.m.,&nbsp;Calle San Ignacio n. 61, Plaza Catedral</p>\r\n<p><strong>Museo de Historia Natural</strong>, desde martes a s&aacute;bado, 10.15-5.45 p.m.,&nbsp;domingo 9.15-12.45, Paseo del Prado entre Dragones y San Mart&iacute;n</p>\r\n<p><strong>Museo de la Revoluci&oacute;n</strong>, da martes a s&aacute;bado, 13-6 p.m., domenica 9-13,&nbsp;Calle Refugio n.1, entre Monserrate y Zuluela</p>\r\n<p><strong>Museo de Artes Decorativas</strong>, da martes a s&aacute;bado, 11-6.30 p.m.,&nbsp;domingo&nbsp; 9-1 p.m., Calle 17 n. 502, entre D&nbsp; y&nbsp; E, Vedado</p>\r\n<p><strong>Museo Napole&oacute;nico</strong>, desde martes&nbsp; a viernes, 9-12/1-4 p.m., a&nbsp;s&aacute;bados alternados, Calle San Miguel esquina Calle Ronda, Vedado</p>\r\n<p><strong>Museo Archivo de la M&uacute;sica</strong>, desde martes a s&aacute;bado, 10-16 p.m. domingo 9-12,&nbsp;Calle C&aacute;rcel n. 1, entre Aguilar y Habana</p>\r\n<p><strong>Museo &Aacute;rabe,</strong>&nbsp;desde martes a s&aacute;bado, 2.30- 6 p.m./17-9.45 p.m., domingo 9-1 p.m.,&nbsp;Calle Oficios, entre Obispo y Obrapia</p>\r\n<p><strong>Casa del &Aacute;frica,</strong>&nbsp;desde martes a s&aacute;bado, 2.30-6.30 p.m. domingo 9-1 p.m.,&nbsp;Calle Obrap&iacute;a n. 157, entre Mercaderes y San Ignacio</p>\r\n<p><strong>Centro Wilfredo Lam</strong>, desde lunes a viernes, 8.30-4.30 p.m., a s&aacute;bados alternados, Calle Oficio y Acosta</p>\r\n<p>&nbsp;</p>\r\n<p align="center">&nbsp;</p>\r\n<h2 align="center">Por la noche, una gran elecci&oacute;n</h2>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>No es f&aacute;cil&nbsp; enumerar los lugares nocturnos de diversi&oacute;n a la Habana, hay por todo lado, es suficiente mirar alrededor suyo, adem&aacute;s casi cada Hotel organiza su propio espect&aacute;culo. De toda forma, tiene aqu&iacute; algunas sugerencias.</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Sal&oacute;n Rosado de la Tropical, Benny Mor&eacute;</strong>, poco se&ntilde;alado en los gu&iacute;as. M&uacute;sica y bailes de locura en un mundo extraordinario en tres niveles, que nosotros llamamos desde rato Infierno, Purgatorio y Para&iacute;so, el de arriba, el Para&iacute;so por supuesto, reservado a los pocos extranjeros aventureros.</p>\r\n<p>Nosotros preferimos el Infierno del primer piso, entra la perdida gente. Acostumbramos entrar por el Para&iacute;so, y de repente nos precipitamos al Infierno, dedic&aacute;ndonos al placer de demoniacos y desenfrenados bailes bajo el escenario, con el grupo al trabajo que canta y grita e que trastorna de m&uacute;sica y ritmo salvaje.</p>\r\n<p>Much&iacute;sima gente por el fin de semana, casi todos cubanos. Recientemente restaurado.</p>\r\n<p>Se&ntilde;oras y Se&ntilde;ores, ustedes son avisadas/os: este es el Pueblo, esta es la verdadera Cuba. Bueno, el Pueblo est&aacute; bien, pero hace falta de un poquito de</p>\r\n<p>Prudencia, la muchedumbre a veces es demasiada, y all&iacute; por cierto hay de todo.</p>\r\n<p>Avenida 41, entre 45 y 44, Playa.</p>\r\n<p><strong>&nbsp;</strong></p>\r\n<p><strong>Habana Caf&eacute;,</strong>&nbsp;m&uacute;sica, Paseo, entre 1 y 3.</p>\r\n<p><strong>Casa de la M&uacute;sica</strong>, m&uacute;sica y bailes, Avenida Italia, entre Concordia y Neptuno.</p>\r\n<p><strong>Cabaret Turquino,</strong>&nbsp;ultimo piso del Hotel Habana Libre, discoteca, si n o llueve abren el techo, Calle 23 y L.</p>\r\n<p><strong>Caf&eacute; Cantante</strong>, discoteca, entrada lateral del Teatro Nacional de Cuba, Paseo y Calle 39.</p>\r\n<p><strong>Jazz Caf&eacute;,&nbsp;</strong>Calle 1 y Paseo.</p>\r\n<p><strong>Cabaret Tropicana</strong>, cl&aacute;sico espect&aacute;culo de cabaret habanero, muy caro, Calle 72 y 43, Marianao, oeste de la Ciudad. Il&nbsp;<strong>Tropicana&nbsp;</strong>queda adentro de un parque bell&iacute;simo, abierto tambi&eacute;n por la tarde, hay un caf&eacute; restaurante.</p>\r\n<p>Piano Bar del<strong>&ldquo;1830&rdquo;,</strong>&nbsp;Malec&oacute;n e 22, torre antigua a orilla del mar.</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<h2 align="center">Solo algunos de los muchos teatros.&nbsp;</h2>\r\n<p align="center"><strong>&nbsp;</strong></p>\r\n<p align="center"><strong>&nbsp;</strong></p>\r\n<p><strong>Gran Teatro de La Habana</strong>, Calle Prado, entre San Rafael y San Jos&eacute; famoso por el Ballet Nacional de Cuba. M&uacute;sica cl&aacute;sica y moderna.</p>\r\n<p><strong>Teatro Am&eacute;rica,</strong>&nbsp;Galliano esquina Concordia, conciertos.</p>\r\n<p><strong>Teatro Nacional de Cuba</strong>, Paseo y 39, m&uacute;sica popular cubana, solistas y humor. Laboratorio de teatro, m&aacute;s Caf&eacute; Cantante y Piano Bar.</p>\r\n<p><strong>Teatro Mella,&nbsp;</strong>L&iacute;nea n. 657, entre A y B, recitals y teatro, espect&aacute;culos de artistas cubanos y internacionales.</p>\r\n<p><strong>Teatro Amadeo Rold&aacute;n</strong>, Calzada, entre Calle D&nbsp; y&nbsp; E, orquestas sinf&oacute;nicas y recitals.</p>\r\n<p><strong>Teatro Karl Marx,</strong>&nbsp;Avenida Primera, entre Calles 8&nbsp; y&nbsp; 10, 5500 asientos, conciertos y &nbsp;espect&aacute;culos de grandes grupos cubanos y internacionales.</p>\r\n<p><strong>Basilica Menor de San Francisco de As&iacute;s</strong>, en la Plaza que lleva el mismo nombre, m&uacute;sica de camera y conciertos.</p>\r\n<p><strong>Teatro Bertold Brecht</strong>, Calle 13 y Primera, Vedado.</p>\r\n<p><strong>Teatro El S&oacute;tano</strong>, Calle K, entre Calles 25 y 27, Vedado, teatro moderno.</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p align="center">Sugerencias</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>Si quieres saber m&aacute;s sobre La Habana, ponte en contacto con nosotros, <span style="text-decoration: underline;">dale clic aqu&iacute;</span></p>\r\n<p>Si quieres conocer nuestras ofertas, <span style="text-decoration: underline;">dale clic aqu&iacute;</span></p>', 1, 0, 8, '2012-10-25 13:17:53');
INSERT INTO `destinations` (`id`, `tipo`, `nombre`, `slug`, `subtitulo_ita`, `subtitulo_esp`, `descripcion_ita`, `descripcion_esp`, `activo`, `comment_count`, `destinationimg_count`, `created`) VALUES
(4, 'Cuba', 'Cienfuegos', '4_cienfuegos', '', '', '<p>La francese perch&eacute; fondata da coloni francesi, unica in questo in tutta Cuba, e il tracciato della Citt&aacute; lo conferma, ricorda parti della Parigi stile Haussmann, il barone&nbsp; che ridisegn&oacute;&nbsp; la Ville Lumi&egrave;re dopo la rivolta della Comune.</p>\r\n<p>Quindi, larghe avenidas e edifici neoclassici. Bellissima la Baia, con le spiagge.</p>\r\n<p>Citt&aacute; colta e interessante, molto pulita, in perenne ammirazione di poeti, scrittori e pittori in genere e di quelli, tanti, che la abitano e mi capita spesso di vedere&nbsp; gente di ogni et&aacute; seduta sulla soglia a leggere poesie e racconti, di autori cubani e non.</p>\r\n<p>Vaste librerie piene di libri di ogni genere a prezzi irrisori, e fate attenzione, in pesos nazionali, non nei convertibili CUC&nbsp;&nbsp;&nbsp; (1CUC=24 pesos nazionali)</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<h2 align="center">Informazioni, e alcuni posti da vedere.</h2>\r\n<h2 align="center">&nbsp;</h2>\r\n<p align="center"><strong>&nbsp;</strong></p>\r\n<p>Fondata nel 1819 da coloni francesi.</p>\r\n<p>Il centro Storico &eacute; Patrimonio Culturale dell&rsquo;Umanit&aacute;, secondo l&rsquo;Unesco.</p>\r\n<p>Abitanti: 186.000</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Giardino Botanico, Fortezza de Jagua, Parco Jos&eacute; Mart&iacute;, Teatro Tom&aacute;s Terry, Paseo del Prado, Club Na&uacute;tico, Catedral Nuestra Se&ntilde;ora de la Pur&iacute;sima Concepci&oacute;n, Museo Hist&oacute;rico, Delfinario.</strong></p>\r\n<p><strong>&nbsp;</strong></p>\r\n<p><strong>Valle Yaguaranabo,</strong> area paesaggistica protetta.</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Necropoli Tom&aacute;s Acea.</strong></p>\r\n<p>&nbsp;</p>\r\n<p>Spiagge:&nbsp; <strong>Rancho Luna e El Ingl&egrave;s.</strong></p>\r\n<p>&nbsp;</p>\r\n<p><strong>Punta Gorda e Malec&oacute;n</strong>, dimore fantastiche sulla baia.</p>\r\n<p><strong>&nbsp;</strong></p>\r\n<p align="center"><strong>&nbsp;</strong></p>\r\n<p align="center"><strong>&nbsp;</strong></p>\r\n<p align="center"><strong>&nbsp;</strong></p>\r\n<p align="center"><strong>&nbsp;</strong></p>\r\n<p align="center"><strong>&nbsp;</strong></p>\r\n<h2 align="center">Divertirsi di notte</h2>\r\n<p>&nbsp;</p>\r\n<p><strong>&nbsp;</strong></p>\r\n<p><strong>&nbsp;</strong></p>\r\n<p><strong>&nbsp;</strong></p>\r\n<p><strong>Guanaroca, Hotel, Jagua,</strong> Calle 37 y 1, Punta Gorda, bell&rsquo;ambiente, molti stranieri, spettacolo e poi disco.</p>\r\n<p><strong>&nbsp;</strong></p>\r\n<p><strong>Artex,</strong> Calle 35, solo disco.</p>\r\n<p><strong>&nbsp;</strong></p>\r\n<p><strong>Club Benny Mor&eacute;,</strong> Ave. 54, entr 27 y 29, Benny era grande cantante e uomo di spettacolo cubano, per&oacute; qui non si acoltano canzoni sue, solo disco.</p>\r\n<p><strong>&nbsp;</strong></p>\r\n<p><strong>Plaza Palmira,</strong> musica all&rsquo;aria aperta.</p>\r\n<p><strong>&nbsp;</strong></p>\r\n<p><strong>Casa de la M&uacute;sica Benny Mor&eacute;</strong>, Calle 37 entre 4&nbsp; y&nbsp; 6, bell&rsquo; ambiente e musica fantastica,.</p>\r\n<p><strong>&nbsp;</strong></p>\r\n<p><strong>Cabaret TropiSur</strong>, anche questo all&rsquo;aria aperta, venerd&iacute;, sabato e domenica.</p>\r\n<p><strong>&nbsp;</strong></p>\r\n<p><strong>Casa de la Cultura Benjamin Duarte</strong>, Av. 54, eventi musicali dal vivo, danza, arti plastiche, in uno splendido edificio di inizio &lsquo;900 che presenta diversi stili architettonici.</p>\r\n<p>&nbsp;</p>\r\n<p align="center"><strong>&nbsp;</strong></p>\r\n<p align="center"><strong>&nbsp;</strong></p>\r\n<p align="center"><strong>&nbsp;</strong></p>\r\n<h2 align="center">Suggerimenti</h2>\r\n<p><strong>&nbsp;</strong></p>\r\n<p><strong>&nbsp;</strong></p>\r\n<p><strong>&nbsp;</strong></p>\r\n<p>Se vuoi sapere di pi&uacute; su Cienfuegos, mettiti in contatto con noi, clicca qui</p>\r\n<p>&nbsp;</p>\r\n<p>Se invece vuoi conoscere le n ostre offerte, clicca qui</p>\r\n<p align="center"><strong>&nbsp;</strong></p>\r\n<p align="center"><strong>&nbsp;</strong></p>\r\n<p>&nbsp;</p>', '<p>Apodada la Francesa, ya que fue fundada por colonos franceses, &uacute;nica en esto en toda Cuba, y lo confirma el trazado de la ciudad, ya que recuerda la Paris de Haussmann, el bar&oacute;n que redise&ntilde;&oacute; la Ciudad de la Luz despu&eacute;s de la rebeli&oacute;n de la Comuna.</p>\r\n<p>Cuenta con amplias avenidas y edificios neo-cl&aacute;sicos. A orilla de una hermosa bah&iacute;a, con playas.</p>\r\n<p>Ciudad educada y atractiva, muy limpia, en perenne admiraci&oacute;n de poetas, escritores y pintores. A menudo se ven a personas de todas las edades sentadas en el umbral de las casas, intentos a la lectura de poemas y cuentos, de autores cubanos o extranjeros.</p>\r\n<p>Vastas librer&iacute;as llenas de libros de todo tipo y a precios irrisorios, y presten atenci&oacute;n, en pesos nacionales, no CUC convertibles &nbsp;(pesos 1CUC = 24 nacionales)</p>\r\n<p align="center">&nbsp;</p>\r\n<h2 align="center">Informaciones, y algunos lugares para visitar</h2>\r\n<p align="center">&nbsp;</p>\r\n<p align="center"><strong>&nbsp;</strong></p>\r\n<p>Fundada en el a&ntilde;o 1819 por colonos franceses.</p>\r\n<p>El Centro Hist&oacute;rico ha sido declarado Patrimonio Cultural de la Humanidad por la Unesco &nbsp;.</p>\r\n<p>Habitantes: 186.000</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Jard&iacute;n Bot&aacute;nico, Fortezza de Jagua, Parco Jos&eacute; Mart&iacute;, Teatro Tom&aacute;s Terry, Paseo del Prado, Club Na&uacute;tico, Catedral Nuestra Se&ntilde;ora de la Pur&iacute;sima Concepci&oacute;n, Museo Hist&oacute;rico, Delfinario.</strong></p>\r\n<p><strong>&nbsp;</strong></p>\r\n<p><strong>Valle Yaguaranabo,</strong>&nbsp;area protegida.</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Necropolis Tom&aacute;s Acea.</strong></p>\r\n<p>&nbsp;</p>\r\n<p><strong>Playas:</strong>&nbsp;&nbsp;<strong>Rancho Luna e El Ingl&egrave;s.</strong></p>\r\n<p>&nbsp;</p>\r\n<p><strong>Punta Gorda e Malec&oacute;n</strong>, mansiones fant&aacute;sticas a orilla del mar.</p>\r\n<p><strong>&nbsp;</strong></p>\r\n<p align="center"><strong>&nbsp;</strong></p>\r\n<h2 align="center">Diversiones por la noche</h2>\r\n<p>&nbsp;</p>\r\n<p><strong>&nbsp;</strong></p>\r\n<p><strong>Guanaroca, Hotel Jagua,</strong>&nbsp;Calle 37 y 1, Punta Gorda, lindo lugar, muchos extranjeros, espect&aacute;culo y disco.</p>\r\n<p><strong>Artex,</strong>&nbsp;Calle 35, s&oacute;lo disco.</p>\r\n<p><strong>Club Benny Mor&eacute;,</strong>&nbsp;Ave. 54, entre 27 y 29, Benny era gran cantante e hombre de espect&aacute;culo de nivel mundial, pero aqu&iacute; no se escuchan sus canciones, solo disco.</p>\r\n<p><strong>Plaza Palmira,</strong>&nbsp;m&uacute;sica al aire libre.</p>\r\n<p><strong>Casa de la M&uacute;sica Benny Mor&eacute;</strong>, Calle 37 entre 4&nbsp; y&nbsp; 6, ambiente amistoso, entusiasmante, y m&uacute;sica fant&aacute;stica.</p>\r\n<p><strong>Cabaret TropiSur</strong>, esto tambi&eacute;n al aire libre, viernes, s&aacute;bado y domingo.</p>\r\n<p><strong>Casa de la Cultura Benjamin Duarte</strong>, Av. 54, eventos musicales en vivo, danza, artes pl&aacute;sticas, en una espl&eacute;ndida mansi&oacute;n del comienzo 1900,&nbsp; construida en distintos estilos arquitect&oacute;nicos.</p>\r\n<p>&nbsp;</p>\r\n<h2 align="center"><strong>&nbsp;</strong>Sugerencias</h2>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>Si quieres saber m&aacute;s sobre Cienfuegos, ponte en contacto con nosotros, <span style="text-decoration: underline;">dale clic aqu&iacute;</span></p>\r\n<p>Si quieres conocer nuestras ofertas, <span style="text-decoration: underline;">dale clic aqu&iacute;</span></p>', 1, 0, 8, '2012-10-25 13:18:31'),
(5, 'Cuba', 'Camag&uuml;ey', '5_camaguey', '', '', '<p>Per andare in giro per la Citt&aacute; non vi baster&aacute; la cartina, ci vuole qualcuno che vi guidi. C&eacute; da perdersi nel fitto ordito di strade, piazze e parchi, nel labirinto sconcertante di stradine e strettissimi passaggi, creato apposta nel XVIII secolo per mandare in confusione totale pirati, manigoldi e assaltanti di ogni specie.</p>\r\n<p>Il pi&uacute; stretto dei passaggi-strada, il Funda de Catre Alley, lungo un&rsquo;ottantina di metri, non ne misura pi&uacute; di due di larghezza, figuriamoci, vero tunnel, bisognerebbe metterci il cartello, vietato ai claustrofobi.</p>\r\n<p>Citt&aacute; magnifica d&rsquo;architettura coloniale, chiese e piazze di gran valore, e la gente &eacute; molto attratta da arti e cultura, cosa d&rsquo;altronde vera in tutta Cuba, in alcuni luoghi pi&uacute; che in altri.</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<h2 align="center">Informazioni e alcuni luoghi da conoscere</h2>\r\n<p align="center"><strong>&nbsp;</strong></p>\r\n<p align="center"><strong>&nbsp;</strong></p>\r\n<p>Fondata nel 1514. Abitanti 326.000.</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Teatro Principal,</strong> ospita il Ballet de Camag&uuml;ey, conosciuto internazionalemente, e l&rsquo;Orquestra Sinf&oacute;nica. Speriamo che siate in Citt&aacute; quando il programma lo prevede.</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Casa Amalia Simoni</strong>, arti decorative e mobilia del XIX secolo.</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Museo Ignacio Agramonte</strong>, Historia&nbsp; e Artes Decorativas.</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Teatro Cubano de M&aacute;gia</strong>, il nome dice tutto.</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Casas de la Cultura</strong>, 15 sedi!</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Gui&ntilde;ol de Camag&uuml;ey</strong>, spettacolo di marionette per bambini.</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p align="center"><strong>&nbsp;</strong></p>\r\n<p align="center"><strong>&nbsp;</strong></p>\r\n<h2 align="center">Divertirsi di notte</h2>\r\n<p align="center"><strong>&nbsp;</strong></p>\r\n<p><strong>&nbsp;</strong></p>\r\n<p><strong>&nbsp;</strong></p>\r\n<p><strong>Club Flamingo</strong>, Independencia n. 208, entre Mart&iacute; y Hermanos Ag&uuml;iero, musica e danza fino all&rsquo;alba, grande ambiente notturno.</p>\r\n<p><strong>Caf&eacute; del Hotel Col&oacute;n,</strong> Calle Rep&uacute;blica 472, patio interno, restaurato recentemente in modo splendido, pochi stranieri.</p>\r\n<p><strong>Disco Impacto,</strong> Calle Rep&uacute;blica entre San Esteban y Correa, disco molto affollata, quasi tutti cubani.</p>\r\n<p><strong>Caf&eacute; Hotel Plaza</strong>, Van Horne n.1, entre Rep&uacute;blica y Avellaneda, bel posto e molto buona la musica.</p>\r\n<p><strong>Casa de la Trova,</strong> Salvador Cisneros n. 171, entre Jos&eacute; Mart&iacute; y Cristo. Musica tradizionale cubana, buoni gruppi musicali e molta gente. Tutto bene, per&oacute; bevande e cibo un po&acute;meno.</p>\r\n<p><strong>El Cambio</strong>, Plaza de Agramonte y Calle Martin 152, buone bevande, e il bello &eacute; che non chiude mai.</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<h2 style="text-align: center;">Suggerimenti</h2>\r\n<p><strong>&nbsp;</strong></p>\r\n<p><strong>&nbsp;</strong></p>\r\n<p><strong>&nbsp;</strong></p>\r\n<p>Se vuoi sapere di pi&uacute; su Camag&uuml;ey, mettiti in contatto con noi, clicca qui</p>\r\n<p>&nbsp;</p>\r\n<p>Se invece vuoi conoscere le nostre offerte, clicca qui</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>', '<p>Para dar vueltas a la ciudad no ser&aacute; suficiente el mapa, necesitar&aacute;n el gu&iacute;a. Pueden perderse en la densa red de calles, plazas y parques, en el desconcertante laberinto de callejuelas y pasajes estrechos, especialmente creadas en el siglo XVIII, para confundir a piratas, villanos y asaltantes de toda especie.</p>\r\n<p>El m&aacute;s estrecho pasaje, la Funda de Catre Alley, ochenta metros de largo, no mide m&aacute;s de dos de ancho. Verdadero t&uacute;nel, hay que poner el letrero: "prohibido a claustrof&oacute;bicos".</p>\r\n<p>Magn&iacute;fica Ciudad de arquitectura colonial, iglesias y plazas de gran valor, y &nbsp;gente muy atra&iacute;da por Artes y &nbsp;Cultura, por otra parte aspecto verdadero en toda Cuba, en algunos lugares m&aacute;s que en otros.</p>\r\n<h2 style="text-align: center;">&nbsp;Informaci&oacute;n y algunos lugares para conocer</h2>\r\n<p>Fundada en el a&ntilde;o 1514. Habitantes&nbsp; 326.000</p>\r\n<p><strong>&nbsp;</strong></p>\r\n<p><strong>Teatro Principal,</strong> &nbsp;donde act&uacute;a el Ballet de Camag&uuml;ey, conocido en el mundo entero, y hospeda la Orquestra Sinf&oacute;nica.</p>\r\n<p><strong>Museo Ignacio Agramonte</strong>, Historia &nbsp;y Artes Decorativas.</p>\r\n<p><strong>Teatro Cubano de M&aacute;gia</strong>, el nombre lo dice todo.</p>\r\n<p><strong>Casas de la Cultura</strong>, 15 sedes</p>\r\n<p><strong>Gui&ntilde;ol de Camag&uuml;ey</strong>, espect&aacute;culos de mu&ntilde;equitos para ni&ntilde;os.</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p align="center"><strong>&nbsp;</strong>Diversiones en la noche&nbsp;</p>\r\n<p><strong>&nbsp;</strong></p>\r\n<p><strong>&nbsp;</strong></p>\r\n<p><strong>Club Flamingo</strong>, Independencia n. 208, entre Mart&iacute; y Hermanos Ag&uuml;iero, musica y danza fina al amanecer, grande ambiente nocturno.</p>\r\n<p><strong>Caf&eacute; del Hotel Col&oacute;n,</strong>&nbsp;Calle Rep&uacute;blica 472, patio interior, recientemente remodelado de forma esplendida, no muchos extranjeros.</p>\r\n<p><strong>Disco Impacto,</strong>&nbsp;Calle Rep&uacute;blica entre San Esteban y Correa, disco muy concurrida, casi todos cubanos.</p>\r\n<p><strong>Caf&eacute; Hotel Plaza</strong>, Van Horne n.1, entre Rep&uacute;blica y Avellaneda, lindo lugar, y buen&iacute;sima m&uacute;sica .</p>\r\n<p><strong>Casa de la Trova,</strong>&nbsp;Salvador Cisneros n. 171, entre Jos&eacute; Mart&iacute; y Cristo. M&uacute;sica tradicional cubana, mucha gente y buenos grupos musicales. Todo esta bien, pero bebidas y comidas no tanto.</p>\r\n<p><strong>El Cambio</strong>, Plaza de Agramonte y Calle Martin 152, buenas bebidas, y lo lindo es que no cierra nunca.</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p align="center">Sugerencias</p>\r\n<p><strong>&nbsp;</strong></p>\r\n<p><strong>&nbsp;</strong></p>\r\n<p>Si quieres saber m&aacute;s sobre Camag&uuml;ey, ponte en contacto con nosotros, <span style="text-decoration: underline;">dale clic aqui</span></p>\r\n<p>Si quieres conocer nuestras ofertas, <span style="text-decoration: underline;">dale clic aqui</span></p>', 1, 0, 6, '2012-10-25 13:18:54'),
(6, 'Cuba', 'Bayamo', '6_bayamo', '', '', '<p>E&acute; la Citt&aacute; della storia di Cuba, anzi della Storia di Cuba, e Capitale della Repubblica &ldquo;in armi&rdquo;, cio&eacute; durante le guerre per l&rsquo;indipendenza dalla Spagna.</p>\r\n<p>Centro di smistamento degli schiavi nei secoli della colonizzazione, poi luogo d&rsquo;inizio e d&rsquo;ispirazione delle guerre d&rsquo;indipendenza, contesa, saccheggiata pi&uacute; d&rsquo;una volta, e poi bruciata dagli stessi abitanti, che la preferirono distrutta che in mano all&rsquo;esercito spagnolo.</p>\r\n<p>Luogo di nascita della &ldquo;cubanit&aacute;&rdquo;, della cultura afro-cubana&nbsp; e della Rivoluzione.</p>\r\n<p>Insomma, passato glorioso e molto movimentato.</p>\r\n<p>Oggi &eacute; Citt&aacute; di grandi tradizioni culturali e artistiche.</p>\r\n<p>Andare in giro per le sue strade sul &ldquo;coche&rdquo;, carretto trainato da cavalli, &eacute; davvero il massimo, e per di pi&uacute; molto economico</p>\r\n<p>&nbsp;</p>\r\n<h2 align="center">Informazioni a alcuni luoghi da vedere</h2>\r\n<h2 align="center"><strong>&nbsp;</strong></h2>\r\n<p>Fondata nel 1513 dal solito Diego Velasquez, con il nome di San Salvador de Bayamo.</p>\r\n<p>Abitanti:&nbsp; 130.000</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Museo delle Cere</strong></p>\r\n<p><strong>&nbsp;</strong></p>\r\n<p><strong>Casa Natale di Carlos Manuel de C&eacute;spedes</strong>, Padre della Nazione.</p>\r\n<p>Calle Francisco Maceo n. 57</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Plaza del Himno,</strong> cos&iacute; chiamata dopo che la &ldquo;Bayamesa&rdquo; &eacute; diventata Inno Nazionale.</p>\r\n<p><strong>Museo Provincial,</strong> contiene documenti importanti per la storia di Cuba, per la formazione di nazionalit&aacute; e coscienza cubane.</p>\r\n<p><strong>Parque C&eacute;spedes.</strong></p>\r\n<p><strong>&nbsp;</strong></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<h2 align="center">Divertirsi di notte</h2>\r\n<p align="center"><strong>&nbsp;</strong></p>\r\n<p>&nbsp;</p>\r\n<p>Non c&rsquo;&eacute; gran che, comunque questo &eacute; ci&oacute; che si pu&oacute; dire.</p>\r\n<p><strong>&nbsp;</strong></p>\r\n<p><strong>Cabaret Bimbo</strong>, Parque Granma.</p>\r\n<p>&nbsp;</p>\r\n<p><strong>La Esquina,</strong> &eacute; una cosa strana, uno entra e pensa d&rsquo;aver sbagliato indirizzo. Poi per&oacute; la gente arriva e allora si trasforma.</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Cuco bar, </strong>molta gente.</p>\r\n<p>&nbsp;</p>\r\n<p><strong>A Bayamo en coche</strong>, rilassato e simpatico.</p>\r\n<p>&nbsp;</p>\r\n<p><strong>La Bayamesa,</strong> discoteca dell&rsquo;Hotel Sierra Maestra.</p>\r\n<p>&nbsp;</p>\r\n<p><strong>La Barbacoa,</strong> cabaret dell&rsquo;Hotel El Yarey.</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Caf&eacute; dell&rsquo;Hotel Royalt&oacute;n.</strong></p>\r\n<p align="center"><strong>&nbsp;</strong></p>\r\n<p align="center"><strong>&nbsp;</strong></p>', '<p>Bayamo es la Ciudad hist&oacute;rica de Cuba, de hecho representa la Historia de Cuba, y Capital de la Rep&uacute;blica "en armas", es decir, durante las guerras de independencia de Espa&ntilde;a.</p>\r\n<p>Centro de comercio de esclavos en los siglos de la colonizaci&oacute;n, despu&eacute;s lugar de comienzo y inspiraci&oacute;n de las guerras de independencia, disputada, saqueada m&aacute;s de una vez, y por fin quemada por los mismos habitantes, quienes la prefirieron destruida antes que conquistada por el ej&eacute;rcito espa&ntilde;ol.</p>\r\n<p>Lugar de nacimiento de la "Cubanitad", de la Cultura afro-cubana y de la Revoluci&oacute;n.</p>\r\n<p>En resumen, pasado glorioso.&nbsp;Hoy en d&iacute;a, es Ciudad muy animada y de grandes tradiciones culturales y art&iacute;sticas.</p>\r\n<p>Irse por las calles en el "coche", carro remolcado por caballos, es realmente lo m&aacute;s divertido del mundo, y muy barato adem&aacute;s.</p>\r\n<p>&nbsp;</p>\r\n<h2>Diversi&oacute;n en la noche</h2>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>No es mucho, sin embargo esto es lo que uno puede decir.</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Cabaret Bimbo, Parque Granma</strong>.</p>\r\n<p>&nbsp;</p>\r\n<p><strong>La Esquina</strong>, es una cosa extra&ntilde;a, uno entra y piensa que tiene la direcci&oacute;n equivocada. Pero entonces la gente viene y luego se vuelve.</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Cuco bar</strong>, un mont&oacute;n de gente.</p>\r\n<p>&nbsp;</p>\r\n<p><strong>A Bayamo en coche</strong>, relajado y amistoso.</p>\r\n<p>&nbsp;</p>\r\n<p><strong>La Bayamesa</strong>, discoteca del hotel Sierra Maestra.</p>\r\n<p>&nbsp;</p>\r\n<p><strong>La Barbacoa,</strong> cabaret Hotel El Yarey.</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Royalton Hotel Caf&eacute;.</strong></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>', 1, 0, 6, '2012-10-25 13:19:12'),
(17, 'Cuba', 'Baracoa', '17_baracoa', '', '', '<p>Questo &eacute; il luogo dove tocc&oacute; terra per la prima volta a Cuba quello che in seguito verr&aacute; chiamato &ldquo;Grande Ammiraglio&nbsp; del Mar Oceano e Vicer&eacute; delle Indie&rdquo;, Cristoforo Colombo, in quel fatale anno domini 1492.</p>\r\n<p>Distrutta completamente da pirati francesi nel &lsquo;600.</p>\r\n<p>Arrivarci via terra non &eacute; del tutto agevole, Baracoa &eacute; isolata.&nbsp;Per&oacute;, una volta sul posto, non ci si pu&oacute; proprio pentire del viaggio.</p>\r\n<p>Costruita fra la prima e la seconda terrazza marina, Baracoa&nbsp; &eacute; su una baia fra le pi&uacute; belle, circondata da fiumi e da foreste tropicali piene di fauna impressionante e, pi&uacute; a est, non lontana da qualcosa che assomiglia a un quasi-deserto.</p>\r\n<p>A profusione, piante di noci di cocco e di cacao, e quindi cioccolato. Molto caff&eacute;, del migliore.</p>\r\n<p>Questa &eacute; la terra di Hatuey, l&rsquo;indio che si ribell&oacute; alla conquista, e fece ammattire gli spagnoli con astute tattiche di guerriglia. Alla fine, fatto prigioniero e barbaramente eliminato. Fray Bartolom&eacute; de La Casa, un religioso un po&rsquo; pi&uacute; umano degli altri, racconta che, poco prima di morire, gli venne mostrata una croce, chiedendogli se volesse andare in Paradiso. Hatuey fece a sua volta una domanda, e volle sapere se questo famoso Paradiso fosse il luogo dove sarebbero andati gli spagnoli: a risposta affermativa, dichiar&oacute; che in quel caso preferiva l&rsquo;Inferno. Vedi monumento al grande Hatuey alla stazione d&rsquo;autobus.</p>\r\n<p>&nbsp;</p>\r\n<h2 style="text-align: center;">Informazioni sulla Citt&aacute;, e posti da vedere</h2>\r\n<p>Fondata nel 1512. Abitanti, 84.000.</p>\r\n<p>&nbsp;</p>\r\n<p><strong>La Cattedrale</strong>,&nbsp;<span style="color: #000000;">punto centrale della Citt&aacute;.</span></p>\r\n<p><strong>Museo Municipal</strong>, <strong>Fuerte Matach&iacute;n.</strong></p>\r\n<p><strong>Spiagge</strong>: <span style="color: #000000;">Maguana, molti turisti. Nibuj&oacute;n, quasi tutti cubani. Manglito.</span></p>\r\n<p><strong>Riserva Biosfera Cuchillos del Toa,</strong> <span style="color: #000000;">foresta tropicale, abitata da molte specie di animali. Ci vuole la guida, ovviamente.</span></p>\r\n<p><strong>Malec&oacute;n,</strong> <span style="color: #000000;">lungomare piacevole e tranquillissimo sulla splendida baia.</span></p>\r\n<p><strong>Casa del Chocolate.</strong></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<h2 style="text-align: center;">Dove divertirsi di notte</h2>\r\n<p><strong>&nbsp;</strong></p>\r\n<p><strong>Casa de la Trova,</strong><span style="color: #000000;"> Maceo 149, gruppi di musica tradizionale. Piccolina e c&rsquo;&eacute; sempre un sacco di gente, per&oacute; secondo me &eacute; una delle migliori di Cuba.</span></p>\r\n<p><strong>Noche de Praga</strong>, <span style="color: #000000;">Maceo 141, discoteca.</span></p>\r\n<p><strong>Caf&eacute; El Patio,</strong> <span style="color: #000000;">Maceo 120, "son" cubano.</span></p>\r\n<p><strong>Casa de la Cultura</strong>, <span style="color: #000000;">Maceo 124.</span></p>\r\n<p><strong>Calle Maceo</strong>, <span style="color: #000000;">come divertirsi per strada, e si pu&oacute; anche mangiare e bere qualcosa.</span></p>\r\n<p><span style="color: #000000;">&nbsp;</span></p>\r\n<p><span style="color: #000000;"> Insomma, quasi tutto sta l&iacute; attorno alla Cattedrale.</span></p>\r\n<p>&nbsp;</p>\r\n<p><span style="color: #000000;">E come se tutto ci&oacute; non bastasse, Baracoa &eacute; importante</span> <strong>sito archeologico</strong> <span style="color: #000000;">di culture pre-colombine.</span></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<h2 style="text-align: center;">Suggerimenti</h2>\r\n<h3><span><strong>&nbsp;</strong></span></h3>\r\n<p><span>Se vuoi sapere di pi&uacute; su Baracoa, mettiti in contatto direttamente con noi, clicca qui</span></p>\r\n<p><span>Se invece vuoi conoscere le nostre offerte, clicca qui</span></p>\r\n<h3><span><strong>&nbsp;</strong></span></h3>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>', '<p>Este es el lugar donde desembarc&oacute; por primera vez en Cuba, el que m&aacute;s tarde ser&iacute;a llamado el "Gran Almirante de la Mar Oc&eacute;ano y Virrey de las Indias," Christopher Columbus, en ese fat&iacute;dico a&ntilde;o domini 1492.</p>\r\n<p>Totalmente destruida por piratas franceses en ''600.</p>\r\n<p>Llegar por tierra no es tarea f&aacute;cil, Baracoa esta aislada. Pero, una vez en el lugar, realmente no se puede arrepentirse del viaje.</p>\r\n<p>Construida entre primera y segunda terraza marina, Baracoa se encuentra a la orilla de una bah&iacute;a de la m&aacute;s bellas, rodeada de r&iacute;os y bosques tropicales llenos de vida silvestre impresionante y, m&aacute;s al este, no muy lejos de algo que parece un casi-desierto.</p>\r\n<p>Una profusi&oacute;n de plantas de coco y cacao, y por lo tanto chocolate. Mucho caf&eacute;, y de gran calidad.</p>\r\n<p>Esta es la tierra de Hatuey, el indio que se rebel&oacute; en contra de la conquista, y puso locos a los espa&ntilde;oles con t&aacute;cticas inteligentes de guerrilla. Al final, encarcelado y brutalmente eliminado. Fray Bartolom&eacute; de La Casa, un cl&eacute;rigo un poco m&aacute;s humanos que otros, dice que poco antes de su muerte, se le mostr&oacute; una cruz, y se le pregunt&oacute; si quer&iacute;a ir al Para&iacute;so. Hatuey puso a su vez una pregunta: quiso saber si este famoso Para&iacute;so ser&iacute;a el lugar donde ir&iacute;an los espa&ntilde;oles. &nbsp;Recibida contesta afirmativa, declar&oacute; que en aquel caso prefer&iacute;a el Infierno. Ver monumento al gran Hatuey a la estaci&oacute;n de autobuses.</p>\r\n<p>&nbsp;</p>\r\n<h2 style="text-align: center;">Informaci&oacute;n sobre la Ciudad, y lugares para ver</h2>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>Fundada en 1512. Poblaci&oacute;n, 84.000.</p>\r\n<p>&nbsp;</p>\r\n<p><strong>La Catedral</strong>, el punto central de la ciudad.</p>\r\n<p><strong>Museo Municipal,</strong> <strong>Fuerte Matach&iacute;n.</strong></p>\r\n<p><strong>Playas</strong>: Maguana, muchos turistas. Nibuj&oacute;n, casi todos cubanos. Manglito.</p>\r\n<p><strong>Cuchillos del Toa Reserva de la Biosfera</strong>, bosque tropical, habitada por muchas especies de animales. El gu&iacute;a es necesario, por supuesto.</p>\r\n<p><strong>Malec&oacute;n</strong> paseo mar&iacute;timo agradable y muy tranquilo en spledida bah&iacute;a.</p>\r\n<p><strong>Casa del Chocolate.</strong></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<h2 style="text-align: center;">Donde divertirse por la noche&nbsp;</h2>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Casa de la Trova</strong>, Maceo 149, grupos de m&uacute;sica tradicional. Muy peque&ntilde;a, y siempre hay un mont&oacute;n de gente, pero es una de los mejores de Cuba</p>\r\n<p><strong>Noche de Praga</strong>, Maceo 141, discoteca.</p>\r\n<p><strong>Caf&eacute; El Patio,</strong> 120 Maceo, "son" cubano.</p>\r\n<p><strong>Casa de la Cultura</strong>, Maceo 124.</p>\r\n<p><strong>Calle Maceo</strong>, como divertirse en la calle, y tambi&eacute;n se puede comer y beber algo.</p>\r\n<p>&nbsp;</p>\r\n<p>En resumen, casi todo est&aacute; all&iacute; alrededor de la <strong>Catedral.</strong></p>\r\n<p>&nbsp;</p>\r\n<p>Y como si todo esto fuera poco, Baracoa es importante <strong>sitio arqueol&oacute;gico</strong> de culturas pre-colombinas.</p>\r\n<p>&nbsp;</p>\r\n<h2 style="text-align: center;">Sugerencias</h2>\r\n<p>&nbsp;</p>\r\n<p>Si quieres saber m&aacute;s sobre Baracoa, ponte en contacto con nosotros directamente, <span style="color: #6666ff;">haga clic aqu&iacute;</span></p>\r\n<p>Si quieres conocer nuestras ofertas especiales, <span style="color: #6666ff;">haga clic aqu&iacute;</span></p>', 1, 0, 7, '2012-10-26 13:19:28'),
(9, 'Yucatan', 'Cob&aacute; - Tulum', '9_coba---tulum', '<p>Antiche Citt&aacute; di Cob&aacute; e Tulum, Oceano e spiaggia</p>', '<p>Antigua ciudad de Cob&aacute; y Tulum, mar y playa</p>', '<p>Visita guidata, nelle ore dalle 9 alle 16.</p>\r\n<p>Visita di Cob&aacute;, Citt&aacute;&nbsp; Maya con il tempio pi&uacute;&nbsp;alto di tutta la Mesoamerica. La Citt&aacute;&nbsp; si trova all&rsquo;interno di un vasto parco. Per percorrerlo, si possono usare biciclette o bici-taxi.</p>\r\n<p>Trasferta poi a Tulum, antica Citt&aacute;&nbsp;portuale Maya sulla costa caraibica orientale.</p>\r\n<p>Distanze: Ek Balam-Cob&aacute;: km 140, Cob&aacute;-Tulum 45, ambedue gli spostamenti in direzione Sud-Est.</p>\r\n<p>Cena e pernottamento al Villaggio&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>Quota individuale di partecipazione:&nbsp;</p>\r\n<p>n. 2 persone&nbsp; Euro&nbsp; 121 / per persona</p>\r\n<p>n. 3 persone&nbsp; Euro&nbsp;&nbsp;&nbsp;&nbsp;94 / per persona</p>\r\n<p>n. 4 persone&nbsp; Euro&nbsp;&nbsp;&nbsp;&nbsp;80 / per persona</p>\r\n<p>n. 5 persone&nbsp; Euro&nbsp;&nbsp;&nbsp;&nbsp;72 / per persona&nbsp;</p>', '<p>Visita guiada, en las horas de 9 a 16.</p>\r\n<p>Visita de Coba, Ciudad Maya con el templo m&aacute;s alto en toda Mesoam&eacute;rica. La Ciudad est&aacute; situada dentro un gran parque. Para seguir visitando, se puede utilizar bicicletas o bici-taxi.</p>\r\n<p>A continuaci&oacute;n, traslado a Tulum, Ciudad Maya y antiguo puerto en el este del Caribe costa. Grande y lind&iacute;sima playa.</p>\r\n<p>Distancias: Ek Balam-Cob&aacute;: 140 km, Coba, Tulum 45, ambos en direcci&oacute;n &nbsp;Sur-Este.</p>\r\n<p>Cena y alojamiento en la Villa</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Precios por persona: Cambio divisas: &nbsp; Euro 1 = USD 1.26</strong></p>\r\n<p>n. 2 personas &nbsp; &nbsp; &nbsp;USD &nbsp; 150 &nbsp; &nbsp; &nbsp;Euro&nbsp; 121 &nbsp; &nbsp; per persona</p>\r\n<p>n. 3 personas &nbsp; &nbsp; &nbsp;USD &nbsp; 118 &nbsp; &nbsp; &nbsp;Euro&nbsp;&nbsp;&nbsp;&nbsp;94 &nbsp; &nbsp; per persona</p>\r\n<p>n. 4 personas &nbsp; &nbsp; &nbsp;USD &nbsp; 100 &nbsp; &nbsp; &nbsp;Euro&nbsp;&nbsp;&nbsp;&nbsp;80 &nbsp; &nbsp; per persona</p>\r\n<p>n. 5 personas &nbsp; &nbsp; &nbsp;USD &nbsp; &nbsp; 90 &nbsp; &nbsp; &nbsp;Euro&nbsp;&nbsp;&nbsp;&nbsp;72 &nbsp; &nbsp; per persona&nbsp;</p>', 1, 0, 6, '2012-10-25 13:48:48'),
(10, 'Yucatan', 'M&eacute;rida', '10_merida', '<p>Citt&aacute;&nbsp; di M&eacute;rida</p>', '<p>Ciudad de M&eacute;rida</p>', '<p>M&eacute;rida &eacute;&nbsp;la Capitale dello Stato di Yucat&aacute;n. Visita guidata al Museo di Antropologia e Storia, visita al centro storico e alle botteghe di artigianato e alla Casa della Cultura.</p>\r\n<p>Distanza Ek Balam-M&eacute;rida: km 170, in direzione Ovest.</p>\r\n<p>Cena e pernottamento al Villaggio.&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>Quota individuale di partecipazione:&nbsp;</p>\r\n<p>n. 2 persone&nbsp; Euro 111 / per persona</p>\r\n<p>n. 3 persone&nbsp; Euro&nbsp;&nbsp;&nbsp;84 / per persona</p>\r\n<p>n. 4 persone&nbsp; Euro&nbsp;&nbsp; 70&nbsp;/ per persona</p>\r\n<p>n. 5 persone&nbsp; Euro&nbsp;&nbsp; 86&nbsp;/ per persona&nbsp;</p>\r\n<p>Guida lingua Inglese, Italiano, Francese , Tedesco&nbsp; Euro 70</p>', '<p>M&eacute;rida es la Capital del Estado de Yucat&aacute;n. Visita guiada al Museo de Antropolog&iacute;a e Historia, visitar el Centro hist&oacute;rico, a las &nbsp;boutiques y a la Casa de la Cultura.</p>\r\n<p>Distancia Ek-Balam Merida km 170, en direcci&oacute;n Oeste.</p>\r\n<p>Cena y alojamiento en el Pueblo.</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Precios por persona: Cambio divisas: Euro 1 = USD &nbsp;1,26</strong></p>\r\n<p>n. 2 personas &nbsp; &nbsp;USD &nbsp; 139 &nbsp; &nbsp; &nbsp; &nbsp;Euros 111 &nbsp; &nbsp; &nbsp;por persona</p>\r\n<p>n. 3 personas &nbsp; &nbsp;USD &nbsp; 105 &nbsp; &nbsp; &nbsp; &nbsp;Euros &nbsp; 84 &nbsp; &nbsp; &nbsp;por persona</p>\r\n<p>n. 4 personas &nbsp; &nbsp;USD &nbsp; &nbsp; 88 &nbsp; &nbsp; &nbsp; &nbsp;Euros &nbsp; 70 &nbsp; &nbsp; &nbsp;por persona</p>\r\n<p>n. 5 personas &nbsp; &nbsp;USD &nbsp; 108 &nbsp; &nbsp; &nbsp; &nbsp;Euros &nbsp; 86 &nbsp; &nbsp; &nbsp;por persona</p>\r\n<p>Gu&iacute;a Ingl&eacute;s, Italiano, Franc&eacute;s, Alem&aacute;n: &nbsp; &nbsp; USD &nbsp;88 &nbsp; &nbsp;Euro 70</p>', 1, 0, 6, '2012-10-25 13:49:10'),
(11, 'Yucatan', 'Solferino', '11_solferino', '<p>Comunit&aacute;&nbsp; indigena di Solferino</p>', '<p>Comunidad ind&iacute;gena de Solferino</p>', '<p>Al mattino, partenza per la visita a Solferino e alla regione dello Yum Balam (Giaguaro Padre) tra paesaggi di savana quasi africana, frequentati da un gran numero di uccelli acquatici, anfibi di ogni genere. Orchidee e bromelie ovunque.</p>\r\n<p>Il programma prevede l&rsquo;attraversamento della foresta fino all&rsquo;Humedal. Da qui, remando in kayak, il visitatore percorre la zona girando attrono a possenti alberi dalle mille radici che escono dall&rsquo;acqua, carichi di uccelli acquatici.</p>\r\n<p>Al ritorno, si sale sui &ldquo;miradores&rdquo;, alte torri di avvistamento.</p>\r\n<p>&Eacute; perfino possibile il &ldquo;cannopy&rdquo;, cio&eacute; passare da un &ldquo;mirador&rdquo; all&rsquo;altro, scivolando appesi a funi sopra la foresta. Il tutto ovviamente con l&rsquo;assistenza della guida, e in tutta sicurezza.</p>\r\n<p>Per finire, visita al Giardino Botanico, al laboratorio delle Erbe e alla <span style="text-decoration: underline;"><strong><a href="../../../noticias/6_tienda-di-prodotti-e-essenze-naturali--villaggio-di-solferino-stato-di-quintana-roo">Tienda di prodotti naturali</a>.</strong></span></p>\r\n<p>Orario delle visite: dalle 9 del mattino alle 17-18. Da Ek Balam a Solferino: km 125. Direzione Nord-Nord-Est.</p>\r\n<p>Cena e pernottamento al Villaggio.&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><strong>&nbsp;Quota individuale di partecipazione:&nbsp;&nbsp;&nbsp;&nbsp; Euro&nbsp;&nbsp;95&nbsp;</strong></p>', '<p>Por la ma&ntilde;ana, se visitar&aacute; Solferino y la regi&oacute;n del Yum Balam (Jaguar Padre) a trav&eacute;s de paisajes de sabana casi africana, con un gran n&uacute;mero de aves acu&aacute;ticas, anfibios de todo tipo. Orqu&iacute;deas y bromelias en todas partes.</p>\r\n<p>El programa prev&eacute; el cruce de la selva hasta el Humedal. A partir de aqu&iacute;, &nbsp;remando en kayak, el visitante atraviesa la zona dando vuelta a poderosos &aacute;rboles de mil ra&iacute;ces sobresalientes del agua, llenos de aves acu&aacute;ticas.</p>\r\n<p>En el camino de vuelta, se puede subir a las altas torres en madera llamadas "Miradores".</p>\r\n<p>Incluso es posible "cannopy", es decir, pasar de un "Mirador" a otro, deslizando colgados a sogas que cuelgan sobre la selva. Todo por supuesto con la ayuda de la gu&iacute;a, y de forma segura.</p>\r\n<p>Finalmente visita al Jard&iacute;n Bot&aacute;nico, el Laboratorio de hierbas y a la Tienda de productos naturales Maya.</p>\r\n<p>Horario de visitas: desde la 9 de la ma&ntilde;ana &nbsp;hasta las 5-6 p.m. &nbsp;Distancia Ek Balam-Solferino: km 125. En direcci&oacute;n Nord-Nord-Est</p>\r\n<p>Cena y alojamiento en el Pueblo</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Precio por persona: &nbsp;Cambio divisas: Euro 1 = USD 1.26</strong></p>\r\n<p>&nbsp;</p>\r\n<p>USD 119 &nbsp; Euro 95</p>', 1, 0, 5, '2012-10-25 13:49:32'),
(12, 'Yucatan', 'Izamal', '12_izamal', '<p>Citt&aacute;&nbsp; di Izamal</p>', '<p>Ciudad de Izamal</p>', '<p>In calesse, dal Convento francescano del XVI secolo, costruito sopra un&rsquo;enorme piattaforma Maya, si passa a visitare la Citt&agrave;&nbsp;Gialla, cosiddetta per il colore di tutte le case del Centro Storico, con visita al Museo dell&rsquo;Artigianato e a laboratori di gioielleria.</p>\r\n<p>Da Ek Balam a Chich&eacute;n Itz&aacute;&nbsp;la distanza &eacute;&nbsp;di km 60, e da Chich&eacute;n Itz&aacute;&nbsp;a Izamal di km 45, ambedue gli spostamenti in direzione Ovest.</p>\r\n<p>Visita guidata dalle ore 9 alle 17.</p>\r\n<p>Cena e pernottamento al Villaggio.&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Quota individuale di partecipazione:&nbsp;</strong></p>\r\n<p>n. 2 persone&nbsp; Euro&nbsp;&nbsp;95 / per persona</p>\r\n<p>n. 3 persone&nbsp; Euro&nbsp; 69 / per persona</p>\r\n<p>n. 4 persone&nbsp; Euro&nbsp;&nbsp;56 / per persona</p>\r\n<p>n. 5 persone&nbsp; Euro&nbsp;&nbsp;60 / per persona&nbsp;</p>\r\n<p>Interprete a richiesta&nbsp; Euro&nbsp; 80&nbsp;</p>', '<p>Paseo en calesa, desde el Monasterio franciscano del siglo XVI, construido sobre una enorme plataforma Maya, ustedes visitar&aacute;n la Ciudad &nbsp;Amarilla, llamada as&iacute; por el color de todas las casas del casco antiguo, y visitar&aacute;n el Museo de los talleres de joyas.</p>\r\n<p>Desde Ek Balam a Chich&eacute;n Itz&aacute; &nbsp;la distancia es de 60 km, y desde Chich&eacute;n Itz&aacute; a Izamal de 45 km, ambos movi&eacute;ndose en direcci&oacute;n oeste.</p>\r\n<p>Visita guiada desde 9 a 17.</p>\r\n<p>Cena y alojamiento en el pueblo.</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Precio por persona: Cambio divisas: Euro 1 = USD 1,26</strong></p>\r\n<p>n. 2 personas &nbsp; &nbsp; &nbsp;USD &nbsp;119 &nbsp; &nbsp; &nbsp; &nbsp; Euro &nbsp;95 euros &nbsp; &nbsp;por persona</p>\r\n<p>n. 3 personas &nbsp; &nbsp; &nbsp;USD &nbsp; &nbsp;87 &nbsp; &nbsp; &nbsp; &nbsp; Euro &nbsp;69 euros &nbsp; &nbsp;por persona</p>\r\n<p>n. 4 personas &nbsp; &nbsp; &nbsp;USD &nbsp; &nbsp;70 &nbsp; &nbsp; &nbsp; &nbsp; Euro &nbsp;56 Euros &nbsp; &nbsp;por persona</p>\r\n<p>n. 5 personas &nbsp; &nbsp; &nbsp;USD &nbsp; &nbsp;75 &nbsp; &nbsp; &nbsp; &nbsp; Euro &nbsp;60 euros &nbsp; &nbsp;por persona</p>\r\n<p>Int&eacute;rprete a pedido &nbsp; USD &nbsp;99 &nbsp; Euro &nbsp;80&nbsp;</p>', 1, 0, 6, '2012-10-25 13:49:48'),
(13, 'Yucatan', 'Chichen Itz&aacute;', '13_chichen-itza', '<p>Zona Archeologica di Chich&eacute;n Itz&aacute;</p>', '<p>Zona Arqueol&oacute;gica de Chich&eacute;n Itz&aacute;</p>', '<p>Tour guidato al sito archeologico della Citta May&aacute;&nbsp;pre-hispanica di Chich&eacute;n Itz&aacute;, zona archeologica d&rsquo;importanza planetaria, Patrimonio dell&rsquo;Umanit&aacute;&nbsp; secondo l&rsquo;Unesco, dichiarata Meraviglia del Mondo.&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Quota individuale di partecipazione:&nbsp;</strong></p>\r\n<p>n. 2 persone&nbsp;&nbsp;&nbsp; Euro&nbsp;75 / per persona</p>\r\n<p>n. 3 persone&nbsp;&nbsp;&nbsp; Euro&nbsp;56 / per persona</p>\r\n<p>n. 4 persone&nbsp;&nbsp;&nbsp; Euro 48 / per persona</p>\r\n<p>n. 5 persone&nbsp;&nbsp;&nbsp; Euro 42 / per persona&nbsp;</p>\r\n<p>Interprete su richiesta&nbsp; Euro 80 &nbsp;</p>', '<p>Visita guiada a la Zona Arqueol&oacute;gica de la Ciudad Maya pre-hispanica de Chich&eacute;n Itz&aacute;, sitio arqueol&oacute;gico de importancia mundial, Patrimonio de la Humanidad seg&uacute;n la UNESCO, declarada Maravilla del Mundo.</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Precio por persona: &nbsp;Cambio divisas: &nbsp;Euro 1 = USD 1.26</strong></p>\r\n<p>n. 2 personas &nbsp; &nbsp; USD 94 &nbsp; &nbsp; Euros 75 &nbsp; &nbsp;por persona</p>\r\n<p>n. 3 personas &nbsp; &nbsp; USD 70 &nbsp; &nbsp; Euro &nbsp;56 &nbsp; &nbsp; por persona</p>\r\n<p>n. 4 personas &nbsp; &nbsp; USD 60 &nbsp; &nbsp; Euros 48 &nbsp; &nbsp;por persona</p>\r\n<p>n. 5 personas &nbsp; &nbsp; USD 53 &nbsp; &nbsp; Euros 42 &nbsp; &nbsp; por persona</p>\r\n<p>Int&eacute;rprete a pedido: &nbsp; &nbsp; USD &nbsp;99 &nbsp; Euros &nbsp; 80 &nbsp;</p>', 1, 0, 6, '2012-10-25 13:50:04'),
(15, 'Yucatan', 'Ek Balam - Xcanch&eacute;', '15_ek-balam---xcanche', '<h3><strong>Zona Archeologica di Ek Balam e dell''Ecoparco Acquatico Xcanch&eacute;</strong><strong>.</strong></h3>', '', '<p>Visita guidata della Zona Archeologica Maya di Ek Balam (il Giaguaro Nero) e dell&rsquo;Ecoparco Acquatico Xcanch&eacute;.</p>\r\n<p>Ek Balam &eacute;&nbsp;una Zona Archeologica Maya ancora poco nota ma di grande importanza storica, e si trova a un km circa dal Villaggio.</p>\r\n<p>L&rsquo;Ecoparco Acquatico Xcanch&eacute;&nbsp;racchiude un bellissismo Cenote, lago sotterraneo e luogo sacro per gli antichi Maya, in quanto considerato apertura verso l&rsquo;Inframundo. Il Cenote pu&oacute;&nbsp;essere attraversato con funi da una sponda all&rsquo;altra, o navigato con kayak.</p>\r\n<p>Arriverete all&rsquo;entrata dell&rsquo;Ecoparco con l&rsquo;auto, e poi occorre attraversare la foresta in bici o in bici-taxi. I visitatori saranno poi ricevuti dalla guida che, utilizzando una mappa, far&aacute;&nbsp;loro vedere l&rsquo;ubicazione di tutte le installazioni e i servizi a cui avranno accesso.</p>\r\n<p>Orario delle visite: dalle ore 9 alle 16 (ora di chiusura dell&rsquo;Ecoparco)</p>\r\n<p>Visita guidata, con le nostre guide Maya bilingue, spagnolo e inglese. Zona Archeologica e Cenote sono vicinissimi al Villaggio.</p>\r\n<p>Cena e pernottamento al Villaggio.&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<h3><strong>Quota individuale di partecipazione:</strong>&nbsp;</h3>\r\n<p>n. 2 persone&nbsp;&nbsp; Euro&nbsp;71 / per persona</p>\r\n<p>n. 3 persone&nbsp;&nbsp; Euro&nbsp;65 / per persona</p>\r\n<p>n. 4 persone&nbsp;&nbsp; Euro&nbsp;54 / per persona</p>\r\n<p>n. 5 persone&nbsp;&nbsp; Euro 48 / per persona&nbsp;</p>', '<p>Visita guiada al sitio arqueol&oacute;gico Maya de Ek Balam (Jaguar Negro) y al Ecoparque Acu&aacute;tico Xcanch&eacute;.</p>\r\n<p>Ek Balam es un sitio arqueol&oacute;gico Maya a&uacute;n poco conocido pero de gran importancia hist&oacute;rica, y est&aacute; situado a un kil&oacute;metro del Pueblo.</p>\r\n<p>El Ecoparque Acu&aacute;tico Xcanch&eacute; contiene un Cenote bellissismo, lago subterr&aacute;neo y lugar sagrado para los antiguos Mayas, ya que se consideraba puerta abierta al Inframundo. El Cenote se puede cruzar con cuerdas de una orilla a otra, o navegar con kayak.</p>\r\n<p>Se llega a la entrada del Ecoparque con el coche, y luego hay que cruzar la selva en bicicleta o en moto-taxi. Los visitantes ser&aacute;n recibidos por el gu&iacute;a, el cual, usando un mapa, les har&aacute; ver la ubicaci&oacute;n de todas las instalaciones y servicios a los que tendr&aacute;n acceso.</p>\r\n<p>Horario: de 9 a 16(hora de cierre del Ecoparque)</p>\r\n<p>Visita gu&iacute;ada, con nuestros gu&iacute;as Mayas biling&uuml;e, espa&ntilde;ol e Ingl&eacute;s. Zona Arcqueol&oacute;gica y Cenote est&aacute;n muy cerca de la Aldea.</p>\r\n<p>Cena y alojamiento en el pueblo.</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Precio por persona: Cambio divisas: 1 Euro=USD 1,26</strong></p>\r\n<p>n. 2 personas &nbsp; &nbsp;USD 89 &nbsp; Euros &nbsp;71 &nbsp; &nbsp; por persona</p>\r\n<p>n. 3 personas &nbsp; &nbsp;USD 82 &nbsp; Euros &nbsp;65 &nbsp; &nbsp; por persona</p>\r\n<p>n. 4 personas &nbsp; &nbsp;USD 68 &nbsp; Euros &nbsp;54 &nbsp; &nbsp; por persona</p>\r\n<p>n. 5 personas &nbsp; &nbsp;USD 60 &nbsp; Euros &nbsp;48 &nbsp; &nbsp; por persona</p>', 1, 0, 6, '2012-10-25 13:50:38'),
(16, 'Yucatan', 'Ek Balam', '16_ek-balam', '<p>Tutta la giornata nella Comunit&aacute;&nbsp;Maya di Ek Balam, con la guida.</p>', '<p>Todo el d&iacute;a en la Comunidad Maya de Ek Balam, con gu&iacute;a.</p>', '<p>Visita con la nostra guida Maya dalle ore 9 del mattino, per tutto il giorno.</p>\r\n<p>Uh Najil Ek Balam &eacute;&nbsp; un villaggio maya che mantiene tradizioni e forme di vita Maya, letteralmente immerso nella foresta, con vegetazione e fauna tipiche di queste zone.</p>\r\n<p>Da non perdere, il Temazcal, sorta di sauna di vapore usato da secoli da queste popolazioni. Inizio alle ore 20, durata tre ore .</p>\r\n<p>La Comunit&aacute;&nbsp;Maya &eacute;&nbsp;adiacente al Villaggio.</p>\r\n<p>Cena e pernottamento al Villaggio.</p>\r\n<p>&nbsp;</p>\r\n<p>Quota individuale di partecipazione:&nbsp;</p>\r\n<p>n. 2 persone&nbsp;&nbsp; Euro 193 / per persona</p>\r\n<p>n. 3 persone&nbsp;&nbsp; Euro 134 / per persona</p>\r\n<p>n. 4 persone&nbsp;&nbsp; Euro&nbsp;104 / per persona</p>\r\n<p>n. 5 persone&nbsp;&nbsp; Euro&nbsp;&nbsp;&nbsp;87 / per persona&nbsp;</p>', '<p>Visita con nuestro gu&iacute;a Maya, desde las 9 de la ma&ntilde;ana, durante todo el d&iacute;a.</p>\r\n<p>Uh Najil Ek Balam es una Aldea Maya que conserva sus tradiciones y formas de vida, literalmente hundido en la selva, con vegetaci&oacute;n y fauna caracterist&iacute;cas de estas zonas.</p>\r\n<p>No te pierdas el Temazcal, una especie de sauna de vapor utilizado durante siglos por estas poblaciones. A partir de las 20, duraci&oacute;n tres horas.</p>\r\n<p>La Comunidad &nbsp;Maya se encuentra cerca del pueblo.</p>\r\n<p>Cena y alojamiento en el pueblo.</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Precio por persona: &nbsp;Cambio divisas: 1 Euro=USD 1.26</strong></p>\r\n<p>n. 2 personas &nbsp; &nbsp; &nbsp; &nbsp;USD 243 &nbsp; &nbsp;Euro 193 &nbsp; &nbsp; por persona</p>\r\n<p>n. 3 personas &nbsp; &nbsp; &nbsp; &nbsp;USD 168 &nbsp; &nbsp;Euro 134 &nbsp; &nbsp; por persona</p>\r\n<p>n. 4 personas &nbsp; &nbsp; &nbsp; &nbsp;USD 131 &nbsp; &nbsp;Euro 104 &nbsp; &nbsp; por persona</p>\r\n<p>n. 5 personas &nbsp; &nbsp; &nbsp; &nbsp;USD 109 &nbsp; &nbsp;Euro &nbsp; 87 &nbsp; &nbsp; por persona</p>', 1, 0, 6, '2012-10-25 16:27:52'),
(18, 'Cuba', 'Pinar del R&iacute;o', '18_pinar-del-rio', '<p style="text-align: left;" align="center"><strong>Pinar del Rio e Vi&ntilde;ales, comanda la Natura</strong></p>', '<p><strong>Pinar del R&iacute;o y Vi&ntilde;ales, comanda la Naturaleza</strong></p>', '<p>Pinar dell&rsquo;altopiano, se cercate divertimenti come discoteche, balli danze e cotillons siete fuori strada, qualcosa c&rsquo;&eacute; ma avete sbagliato Citt&aacute;, garantito.</p>\r\n<p>A volte per le strade di Pinar non si vede nessuno, qualche carretto con cavallo, qualche bicicletta, come nelle vecchie foto, e l&rsquo;aria &eacute; fresca e trasparente.</p>\r\n<p>A parte il fatto che esistono edifici neoclassici molto decorati, sulla Citt&aacute; in s&eacute; non c&rsquo;&eacute; molto da dire.</p>\r\n<p>Qui domina e comanda la Natura.</p>\r\n<p><strong>&nbsp;</strong></p>\r\n<p><strong>La vicina Vi&ntilde;ales</strong> &eacute; media montagna, piena di pinete e tramonti colorati, strade strette e case di legno, formidabile.</p>\r\n<p>Proprio come dicono i migliori d&eacute;pliants turistici, &rdquo;abbandonatevi al&nbsp; piacere di luoghi&nbsp; incontaminati...ecc&rdquo;, ecco, ben detto.</p>\r\n<p>Se poi togliete dal paesaggio qualche pianta e arbusto locale non sembra neppure di essere ai Caraibi, pare di stare su bassi Pirenei, Prealpi o addirittura sui Carpazi.</p>\r\n<p>&nbsp;</p>\r\n<p><strong>&nbsp;</strong></p>\r\n<h2 style="text-align: center;">&nbsp;Informazioni sulla Citt&aacute;, e alcuni posti da vedere</h2>\r\n<h2 style="text-align: center;">&nbsp;</h2>\r\n<p>Fondata nel 1.699.</p>\r\n<p>Abitanti: 120.000</p>\r\n<p>Pinar &eacute; la Citt&aacute; pi&uacute; occidentale di Cuba, per capirci la pi&uacute; vicina al Messico, in una delle zone agricole pi&uacute; importanti del Paese. Qui cresce&nbsp; il miglior tabacco del mondo e si fanno i migliori sigari cubani.</p>\r\n<p><strong>Casa de la Cultura, Teatro Milan&eacute;s, Jardin Botanico.</strong></p>\r\n<h3>&nbsp;</h3>\r\n<h2 align="center">Divertirsi di notte</h2>\r\n<p align="center"><strong>&nbsp;</strong></p>\r\n<p>Nel <strong>Centro Cultural Polo Monta&ntilde;ez, El Vi&ntilde;alero</strong>, bar con musica dal vivo, di notte, e tribunale di giorno. Bueno, esta es Cuba. Ogni marted&iacute;&nbsp; e due sabati al mese.</p>\r\n<p><strong>Patio Decimista</strong>, di fronte al Vi&ntilde;alero, gruppi musicali, sempre pieno di gente.</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<h2 style="text-align: center;">Suggerimenti</h2>\r\n<h3><span><strong>&nbsp;</strong></span></h3>\r\n<p><span>Se vuoi sapere di pi&uacute; su Pinar del Rio, mettiti in contatto direttamente con noi, clicca qui</span></p>\r\n<p><span>&nbsp;</span></p>\r\n<p><span>Se invece vuoi conoscere le nostre offerte, clicca qui</span></p>', '<p>Pinar de la Meseta<strong>:</strong>&nbsp;si ustedes buscan entretenimientos como discotecas, bailes, danzas y cotillones est&aacute;n fuera del camino, hay algo de esto pero se equivocaron de ciudad, garantizado.</p>\r\n<p>A veces en las calles de Pinar no hay nadie, s&oacute;lo algunos carros con caballos, o alguien en bicicleta, como en las viejas fotograf&iacute;as, y el aire es fresco y claro.</p>\r\n<p>A parte del hecho de que hay muchos edificios neocl&aacute;sicos muy ornamentados, la ciudad en si misma no tiene mucho que decir.</p>\r\n<p>Aqu&iacute; domina y manda la Naturaleza.</p>\r\n<p>&nbsp;</p>\r\n<p><strong>La vecina Vi&ntilde;ales</strong>&nbsp;es la Sierra, pinos y puestas de sol coloreadas, calles estrechas y casas de madera, formidable.</p>\r\n<p>Como dicen los folletos tur&iacute;sticos: "disfrutar del placer de lugares v&iacute;rgenes, etc ...", aqu&iacute;, bien dicho.</p>\r\n<p>Si adem&aacute;s quit&aacute;n del paisaje algunas plantas o alg&uacute;n arbusto local, ni siquiera parece estar en el Caribe, parece estar en los Pirineos, los Alpes o incluso los C&aacute;rpatos.</p>\r\n<p>&nbsp;</p>\r\n<h2 style="text-align: center;">Informaciones sobre la ciudad, y algunos lugares para ver</h2>\r\n<p>&nbsp;</p>\r\n<p>Fundada en 1699.</p>\r\n<p>Poblaci&oacute;n: 120.000</p>\r\n<p>Pinar es la ciudad m&aacute;s occidental de Cuba, para entender el m&aacute;s cercano a M&eacute;xico, una de las zonas agr&iacute;colas m&aacute;s importantes del pa&iacute;s. Aqu&iacute; se cultiva el mejor tabaco del mundo y tomar las mejores puros cubanos.</p>\r\n<p><strong>Casa de la Cultura, el Teatro Milan&eacute;s Jardin Botanico.</strong></p>\r\n<p>&nbsp;</p>\r\n<h2 style="text-align: center;">Diversi&oacute;n en la noche</h2>\r\n<p>&nbsp;</p>\r\n<p>En el<strong> Centro Cultural Polo Monta&ntilde;ez</strong>, <strong>El Vi&ntilde;alero</strong>, bar con m&uacute;sica en vivo por las noches y por los d&iacute;as, alli hay el Tribunal. Bueno, esta es Cuba. Todos los martes y dos s&aacute;bados al mes.</p>\r\n<p><strong>Patio Decimista</strong>, frente a la Vi&ntilde;alero, bandas, siempre lleno de gente.</p>\r\n<p>&nbsp;</p>\r\n<h2 style="text-align: center;">Sugerencias</h2>\r\n<p>&nbsp;</p>\r\n<p>Si quieres saber m&aacute;s de Pinar del R&iacute;o, ponerse en contacto directamente con nosotros, <span style="color: #3366ff;">haga clic aqu&iacute;</span></p>\r\n<p>&nbsp;</p>\r\n<p>Si quieres conocer nuestras ofertas especiales,<span style="color: #3366ff;"> haga clic aqu&iacute;</span></p>', 1, 0, 7, '2012-10-29 15:36:53');
INSERT INTO `destinations` (`id`, `tipo`, `nombre`, `slug`, `subtitulo_ita`, `subtitulo_esp`, `descripcion_ita`, `descripcion_esp`, `activo`, `comment_count`, `destinationimg_count`, `created`) VALUES
(19, 'Cuba', 'Santa Clara', '19_santa-clara', '<p>Santa Clara &eacute; una delle Citt&aacute; pi&uacute; solari di Cuba e anche fra le pi&uacute; tranquille</p>', '<p>Santa Clara es una de las ciudades mas soleadas de Cuba y tambi&eacute;n es de las m&aacute;s tranquilas&nbsp;</p>', '<p>Santa Clara &eacute; una delle Citt&aacute; pi&uacute; solari di Cuba e anche fra le pi&uacute; tranquille, di giorno. Non a caso&nbsp;chiamata &ldquo;clara&rdquo;, la luminosa, e in effetti il nome non tradisce le aspettative.</p>\r\n<p>Il Che Guevara la conquist&oacute; nell&rsquo;ultima battaglia della Rivoluzione, in quell&rsquo;alba del 1959, e la canzone dice che tutta la Citt&aacute; si svegli&oacute; per guardare.&nbsp;</p>\r\n<p>Importante snodo stradale, ci passa l&rsquo;Autostrada che dall&rsquo;Habana porta a Oriente, e la ferrovia. E&acute; collegata direttamente con Sancti Spiritus e Trinidad, cio&eacute; con il versante sud di Cuba, con una buona strada, e non &eacute; troppo lontana&nbsp; da quello settentrionale.</p>\r\n<p>Insomma, un posto strategico, ecco perch&eacute; Fidel Castro mand&oacute; il Che e la sua colonna a prenderla.</p>\r\n<p>Fondata nel 1689. Abitanti: 210.000</p>\r\n<p>&nbsp;</p>\r\n<p align="center"><strong>&nbsp;</strong></p>\r\n<h2 align="center">Informazioni sulla Citt&aacute;, e alcuni posti da vedere.</h2>\r\n<h2 align="center"><strong>&nbsp;</strong></h2>\r\n<p>&nbsp;</p>\r\n<p><strong>Monumento al Che Guevara</strong>, con la sua tomba, e &nbsp;<strong>Treno Blindato.</strong></p>\r\n<p><strong>Mercato &ldquo;Sancino&rdquo;,</strong> frutta e verdura con musica, e artigianato.</p>\r\n<p><strong>Teatro &ldquo;La Caridad&rdquo;.</strong></p>\r\n<p><strong>Plaza del Carmen</strong>, luogo simbolo della Citt&aacute;.</p>\r\n<p><strong>Boulevard,</strong> zona pedonale con molti negozi, bar e ristoranti.</p>\r\n<p><strong>Museo de Artes Decorativas, Parque Vidal, Galer&iacute;a de Artes M&aacute;ximo Gomez.</strong></p>\r\n<p><strong>&nbsp;</strong></p>\r\n<p>&nbsp;</p>\r\n<h2 style="text-align: center;">Dove divertirsi di notte</h2>\r\n<p align="center"><strong>&nbsp;</strong></p>\r\n<p>&nbsp;</p>\r\n<p>Santa Clara si trasforma dopo il calar del sole, ha una vita notturna vivace e per certi aspetti unica tra le Citt&aacute; di provincia.</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Cabaret El Bosque</strong>, a pochi minuti a piedi dal <strong>Parque Vidal</strong>, spettacoli di ballo da mercoled&iacute; a domenica,&nbsp; e poi se vuoi puoi ballare fino all&rsquo;alba nel <strong>Bar El Patio</strong>.&nbsp;Funziona anche di giorno, con piscina e amache all&rsquo;ombra delle palme.</p>\r\n<p><strong>Bar Club Boulevard</strong>, Indipendenza n. 225, entre Maceo y Uni&oacute;n. Performances dal vivo, e si balla. Fine settimana con molta gente.</p>\r\n<p><strong>Piano Bar &ldquo;El Dorado&rdquo;,</strong> proprio in centro Citt&aacute;.</p>\r\n<p><strong>Pe&ntilde;a El Mejunje,</strong> Martha Abreu n.12, &eacute; un posto assolutamente speciale e vale la pena di andarci, conosciuto in tutta Cuba, pieno di turisti e cubani, non fa discriminazioni fra omo e eterosessuali. Drag Queen.&nbsp;Bel posto divertente, senza problemi.&nbsp;Ogni notte teatro, recitazione di poesie, gruppi rock.&nbsp;Domenica pomeriggio, spettacoli per bambini della Compagnia Gui&ntilde;ol (vedi documento: &ldquo;Se viaggiate con bambini&rdquo;)</p>\r\n<p><strong>Salon Primavera,</strong> M&aacute;ximo Gomez n. 51. Disco molto apprezzata dai giovani.</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<h2 style="text-align: center;"><span style="color: #000000;">Suggerimenti</span></h2>\r\n<h3><span style="color: #000000;"><strong>&nbsp;</strong></span></h3>\r\n<p><span style="color: #000000;">Se vuoi sapere di pi&uacute; su Santa Clara, mettiti in contatto direttamente con noi, <a href="../../../nosotros">clicca qui</a></span></p>\r\n<p><span style="color: #000000;">Se invece vuoi conoscere le nostre offerte, <a href="../../../paquetes/3_cuba-3-citta-in-10-giorni-9-notti">clicca qui</a></span></p>\r\n<h3><span style="color: #000000;"><strong>&nbsp;</strong></span></h3>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p align="center"><strong>&nbsp;</strong></p>', '<p>Santa Clara es una de las Ciudades m&aacute;s soleadas de Cuba y tambi&eacute;n de las m&aacute;s tranquilas, durante el d&iacute;a. No al azar es &nbsp;llamada "clara", la luminosa, y efectivamente el nombre no traiciona las expectativas.<br /> <br /> El Che Guevara la conquist&oacute; en la &uacute;ltima batalla de la Revoluci&oacute;n, en aquella alba del 1959, y la canci&oacute;n dice que toda la Ciudad se despert&oacute; para mirar.&nbsp;</p>\r\n<p>Importante articulaci&oacute;n vial, cercana a &nbsp;la autopista que desde La Havana lleva a Oriente, y al ferrocarril. Est&aacute; conectada con una buena carretera directamente con Sancti Spiritus y Trinidad, es decir con el lado sur de Cuba, y no est&aacute; demasiado lejana del lado septentrional.<br /><br /> Entonces, un sitio estrat&eacute;gico: he aqu&iacute; porque Fidel Castro mand&oacute; el Che y su columna a cogerla.<br /> <br /> Fundada en 1689. &nbsp;Habitantes: 210.000</p>\r\n<h2 align="center"><strong>&nbsp;</strong></h2>\r\n<h2 style="text-align: center;">Informaciones sobre la ciudad, y algunos lugares para ver.</h2>\r\n<p>&nbsp;</p>\r\n<p><strong>Monumento al Che Guevara</strong>, con su tumba, y el <strong>Tren Blindado.</strong></p>\r\n<p><strong>Mercado "Sancino",</strong> frutas y verduras con m&uacute;sica y artesan&iacute;as.</p>\r\n<p><strong>Teatro "La Caridad".</strong></p>\r\n<p><strong>Plaza del Carmen</strong>, un s&iacute;mbolo de la ciudad.</p>\r\n<p><strong>Boulevard</strong>, una zona peatonal con numerosas tiendas, bares y restaurantes.</p>\r\n<p><strong>Museo de Artes Decorativas, Parque Vidal, Galer&iacute;a de Artes M&aacute;ximo G&oacute;mez.</strong></p>\r\n<p>&nbsp;</p>\r\n<h2 style="text-align: center;">Donde divertirse por la noche</h2>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>Santa Clara se transforma despu&eacute;s de la puesta del sol, tiene una animada vida nocturna y de alguna manera &uacute;nica entre las ciudades de &nbsp;provincia.</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Cabaret El Bosque</strong>, a pocos minutos a pie del <strong>Parque Vidal</strong>, espect&aacute;culos de danza, de mi&eacute;rcoles a domingo, y luego, si lo deseas, puedes bailar hasta el amanecer en<strong> Bar El Patio</strong>. Tambi&eacute;n trabaja durante el d&iacute;a, con piscina y hamacas bajo las palmeras.</p>\r\n<p><strong>Bar Club Boulevard</strong>, Independencia n. 225, entre Maceo y Uni&oacute;n. Actuaciones en directo y baile. Fin de semana con mucha gente.</p>\r\n<p><strong>Piano Bar "El Dorado"</strong>, en pleno centro de la ciudad.</p>\r\n<p><strong>Pe&ntilde;a El Mejunje</strong>, Martha Abreu, 12, &nbsp;es un lugar muy especial y merece la pena ir all&iacute;, conocido en toda Cuba, lleno de turistas y cubanos, no discrimina entre homo y heterosexuales. Drag Queen. Divertido y bonito lugar, sin problemas. Cada noche el teatro, recitaci&oacute;n de poemas, bandas de rock. <strong>S&oacute;lo los domingos por la tarde,</strong> espect&aacute;culos infantiles del Gui&ntilde;ol Company (ver documento: "Si va a viajar con ni&ntilde;os")</p>\r\n<p><strong>Salon Primavera</strong>, M&aacute;ximo G&oacute;mez n. 51. Disco muy apreciada por los j&oacute;venes.</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<h2 style="text-align: center;">Sugerencias</h2>\r\n<p>&nbsp;</p>\r\n<p>Si desea saber m&aacute;s acerca de Santa Clara, ponte en contacto con nosotros directamente, <a href="../../../nosotros">haga clic aqu&iacute;</a></p>\r\n<p>Si quieres conocer nuestras ofertas especiales, <a href="../../../paquetes/3_cuba-3-citta-in-10-giorni-9-notti">haga clic aqu&iacute;</a></p>', 1, 0, 6, '2012-10-29 15:41:58');

-- --------------------------------------------------------

--
-- Estructura de tabla para la tabla `faqs`
--

CREATE TABLE IF NOT EXISTS `faqs` (
  `id` int(10) unsigned NOT NULL auto_increment,
  `tipo` enum('General','Cuba','Yucatan') default 'General',
  `nombre_ita` varchar(255) NOT NULL,
  `nombre_esp` varchar(255) NOT NULL,
  `descripcion_ita` text,
  `descripcion_esp` text,
  `orden` int(10) unsigned default '0',
  `created` datetime default NULL,
  `activo` tinyint(1) default '1',
  PRIMARY KEY  (`id`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 AUTO_INCREMENT=33 ;

--
-- Volcado de datos para la tabla `faqs`
--

INSERT INTO `faqs` (`id`, `tipo`, `nombre_ita`, `nombre_esp`, `descripcion_ita`, `descripcion_esp`, `orden`, `created`, `activo`) VALUES
(1, 'Cuba', 'Quanti chili di bagaglio posso portare?', '&iquest;Cu&aacute;ntas libras de equipaje puedo llevar?', '<p>30 kilogrammi a persona. Fate attenzione a sorpassare il peso consentito, perch&eacute; la dogana cubana, in caso di controlli, vi far&aacute; pagare 10 Pesos Convertibili per kilogrammo eccedente( circa&nbsp; Euro 8)&nbsp;</p>', '<p>30 kilogramos por persona. Pongan &nbsp;atenci&oacute;n a no sobrepasar el peso permitido, porque la aduana cubana, en caso de control, le har&aacute; pagar 10 Pesos Convertibles por kilogramo en exceso(aproximadamente &euro; 8)</p>', 1, '2012-10-25 13:31:16', 1),
(2, 'Cuba', 'Quale moneta conviene portare?', '&iquest;Qu&eacute; moneda debe portar?', '<p>L&rsquo;euro va benissimo ovviamente, come le principali monete, ad eccezione dei dollari USA, che vengono tassati al 10% al momento del cambio</p>', '<p>Principales monedas y Euro son muy bien aceptadas, por supuesto. Verificar en: &nbsp;www.cubacurrency.com. &nbsp;Cuidado con los d&oacute;lares de Estados Unidos, que se gravan con tasa del 10% cuando se cambia.</p>', 1, '2012-10-25 13:31:53', 1),
(3, 'Cuba', 'Sono valide tutte le carte di credito?', '&iquest;Son validas todas las tarjetas de cr&eacute;dito?', '<p>Le principali s&iacute;, come Mastercard, Visa ecc, &nbsp;ad eccezione di quelle provenienti dagli USA, come American Express. Il prelievo di contanti in banca con carta di credito &eacute; tassato all&rsquo;11%.</p>', '<p>Las m&aacute;s importantes s&iacute;, como Mastercard, o Visa, etc, con la excepci&oacute;n de las procedentes de los EE.UU. como American Express. El cobro de efectivo con tarjeta de cr&eacute;dito, en el Banco y en Cadeca, se grava en un 11%.</p>', 2, '2012-10-25 13:32:22', 1),
(4, 'Cuba', 'Quali monete sono in uso a Cuba?', '&iquest;Qu&eacute; monedas se usan en Cuba?', '<p>A Cuba esistono due monete: il Peso cubano Nazionale e il Peso cubano Convertibile. Quest&rsquo;ultimo &eacute; quello che vi daranno quando cambierete i vostri soldi. Attenzione a quando vi danno il resto, il Convertibile, detto CUC, vale 24 Pesos Nazionali.</p>', '<p>En Cuba hay dos monedas: el Peso cubano Nacional y el Peso cubano Convertible (CUC). Este ultimo es lo que les dar&aacute;n al cambiar su dinero. Atenci&oacute;n a cuando les dar&aacute;n la vuelta, el Convertible, CUC, vale 24 Pesos nacionales.</p>', 3, '2012-10-25 13:32:51', 1),
(5, 'Cuba', 'Come posso riconoscere le banconote delle due monete?', '&iquest;C&oacute;mo puedo reconocer los billetes de ambas monedas?', '<p>Entrate nel sito <a href="http://www.cubacurrency.com/">www.cubacurrency.com</a>:&nbsp; l&iacute; troverete le fotografie delle due banconote e il cambio con la vostra moneta, giorno per&nbsp; giorno. Il cambio &eacute; sempre leggermente diverso da quello che vi applicheranno.</p>', '<p>Entren al sitio www.cubacurrency.com sitio: all&iacute; se encuentran las im&aacute;genes de los billetes y el cambio de moneda d&iacute;a a d&iacute;a. El cambio siempre es un poco diferente de lo que aplicar&aacute;n.</p>', 4, '2012-10-25 13:33:11', 1),
(6, 'Cuba', 'Dove posso cambiare il denaro?', '&iquest;D&oacute;nde puedo cambiar mi dinero?', '<p>Nelle sedi del Banco de Cr&eacute;dito y de Comercio, e in quelle delle Cadeca, Casas de Cambio. Mai, mai, per strada.</p>', '<p>En el Banco de Cr&eacute;dito y de Comercio, y en las Cadeca, Casas de Cambio. Nunca, nunca, en las calles.</p>', 5, '2012-10-25 13:34:45', 1),
(7, 'Cuba', 'Posso portare il cellulare?', '&iquest;Puedo llevar a mi tel&eacute;fono m&oacute;bil?', '<p>S&iacute;, senza problemi. Attenzione: <strong>assolutamente</strong> <strong>proibiti</strong> i telefoni satellitari.</p>', '<p>S&iacute;, no hay problema. Cuidados: <strong>absolutamente prohibidos </strong>los tel&eacute;fonos satelitales.</p>', 6, '2012-10-25 13:35:06', 1),
(8, 'Cuba', 'Cuba &eacute; un posto sicuro?', '&iquest;Cuba es un lugar seguro?', '<p>Cuba &eacute; uno dei posti pi&uacute; sicuri al mondo. Si pu&oacute; andare in giro tranquillamente, sempre usando il buon senso di evitare luoghi troppo appartati e oscuri, come in qualsiasi posto del mondo. Quando sarete fuori dalle Case dove alloggiate, attenzione a macchine fotografiche, denaro, gioielli eccetera.</p>', '<p>Cuba es uno de los lugares m&aacute;s seguros del mundo. Pueden irse tranquilamente todo lado, usando siempre el sentido com&uacute;n para evitar lugares que sean demasiado oscuros y aislados, como en cualquier lugar del mundo. Cuando ustedes se encuentren fuera de las Casas donde se alojan, tienen que cuidar c&aacute;maras, dinero, joyas, etc.</p>', 7, '2012-10-25 13:35:30', 1),
(9, 'Cuba', 'Sono necessarie vaccinazioni?', '&iquest;Son necesarias algunas vacunaciones?', '<p>Nessuna vaccinazione &eacute; necessaria.</p>', '<p>No se requiere alguna vacunaci&oacute;n.</p>', 8, '2012-10-25 13:35:51', 1),
(10, 'Cuba', '&Eacute; necessaria un&rsquo;assicurazione di viaggio?', '&iquest;Se necesita un seguro de viaje?', '<p>S&iacute;, e il costo &eacute; di USD 2.50, al giorno e per persona. Vi sar&aacute; fornita al momento di comprare il volo. Non perdetela, o la dovrete ripagare.</p>', '<p>S&iacute;, y el costo es de USD 2,50 por d&iacute;a y por persona. Se le proporcionar&aacute; en el momento de la compra del pasaje. No se lo pierdan, o tendr&aacute;n que pagar otra vez.</p>', 9, '2012-10-25 13:36:08', 1),
(11, 'Cuba', 'Per visitare Cuba, occorre il visto d&rsquo;entrata?', 'Para visitar Cuba, es necesario el visado de entrada?', '<p>S&iacute;, e vi sar&aacute; fornito anch&rsquo;esso con il biglietto aereo. Raccomandazione: fate attenzione a non perderlo, perch&eacute; vi sar&aacute; richiesto all&rsquo;uscita dal Paese.</p>', '<p>S&iacute;, y ser&aacute; proporcionado con el pasaje aereo. Recomendaci&oacute;n: tengan cuidado de no perderlo, ya que se les pedir&aacute; a la salida del Pais.</p>', 10, '2012-10-25 13:36:27', 1),
(12, 'Yucatan', 'Quanti chili di bagaglio posso portare?', '&iquest;Cu&aacute;ntas libras de equipaje puedo llevar?', '<p>Quelli consentiti dalla vostra Compagnia aerea. Non portare &nbsp;liquidi contenuti in bottiglie&nbsp; o contenitori non sigillati.&nbsp;</p>', '<p>Los que les permite la aerol&iacute;nea. No transporten l&iacute;quidos en botellas o contenedores que no sean sellados.</p>', 11, '2012-11-01 16:52:37', 1),
(13, 'Yucatan', 'Quale moneta conviene portare?', '&iquest;Cual divisa conviene llevar?', '<p>Qualunque moneta, non ci sono restrizioni.&nbsp;</p>', '<p>Todas, no hay restricciones.</p>', 12, '2012-11-01 16:52:55', 1),
(14, 'Yucatan', 'Sono valide tutte le carte di credito?', 'Son validas todas las tarjetas de cr&eacute;dito?', '<p>S&iacute;, tutte.</p>', '<p>S&iacute;, todas.</p>', 23, '2012-11-01 16:53:27', 1),
(15, 'Yucatan', 'Che moneta &eacute; in uso in Messico?', '&iquest;Qu&eacute; moneda se utiliza en M&eacute;xico?', '<p>Il Peso messicano, che potete acquistare in qualsiasi Banca.</p>', '<p>El Peso mexicano, que se puede comprar en cualquier Banco.</p>', 19, '2012-11-01 16:53:45', 1),
(16, 'Yucatan', 'Dove posso cambiare il denaro?', '&iquest;D&oacute;nde puedo cambiar mi dinero?', '<p>In qualsiasi Banca, presentando il passaporto. Cambiate il denaro preferibilmente nelle banche, e non nei cambiavalute sulla strada: il cambio &eacute; pi&uacute; favorevole per voi. Potete consultare il cambio con l&rsquo;Euro o altre monete, come il Franco Svizzero, in qualsiasi sito specializzato in divise.&nbsp;Qui a fianco, trovate un bottone&nbsp;per verificare i cambi.</p>', '<p>En cualquier Banco, presentando el pasaporte. Cambien el dinero preferentemente en los Bancos, y no concambistas en la calle: el tipo de cambio es m&aacute;s favorable para ustedes. Ustedes pueden verificar el tipo de cambio con el euro u otras monedas, como el Franco Suizo, en cualquier sitio especializado en divisas. A la izquierda, se encuentra un bot&oacute;n para verificar los cambios.</p>', 13, '2012-11-01 16:54:03', 1),
(17, 'Yucatan', 'Yucat&aacute;n &eacute; un posto sicuro?', '&iquest;Yucat&aacute;n es un lugar seguro?', '<p>Assolutamente tranquillo e sicuro. Con le precauzioni che usereste in qualsiasi posto del mondo.</p>', '<p>Absolutamente tranquilo y seguro. Con las precauciones que usari&aacute;n en cualquier parte del mundo.</p>', 21, '2012-11-01 16:54:18', 1),
(18, 'Yucatan', 'Sono necessarie vaccinazioni?', 'Que vacunas se necesitan?', '<p>Nessuna vaccinazione &eacute; necessaria.</p>', '<p>No es necesaria alguna vacunaci&oacute;n.</p>', 15, '2012-11-01 16:54:33', 1),
(19, 'Yucatan', '&Eacute; necessaria un&rsquo;assicurazione di viaggio?', 'Se necesita un seguro de viaje?', '<p>Per quanto riguarda Cuba, &eacute; necessaria un''assicurazione sanitaria, che vi sar&aacute; fornita al momento dell''acquisto del biglietto aereo. Per Yucat&aacute;n, non &eacute; necessaria.</p>', '<p>Por Cuba, es necesario un seguro de salud, otorgada al momento de la compra del pasaje aereo. Por Yucat&aacute;n no es necesario.</p>', 16, '2012-11-01 16:54:48', 1),
(20, 'Yucatan', 'Per visitare lo Yucat&aacute;n, occorre il visto d&rsquo;entrata?', 'Para visitar la Pen&iacute;nsula de Yucat&aacute;n, es necesario el visado de entrada?', '<p>In linea generale, per gli Europei non serve il visto.</p>', '<p>En general, los europeos no necesitan visado.</p>', 31, '2012-11-01 16:55:06', 1),
(21, 'Cuba', 'Occorre pagare qualcosa in Aereoporto, all&#039;uscita dal Paese?', 'Hay que pagar algo en Aeropuerto, a la salida del Pa&iacute;s?', '<p>S&iacute;, dopo il check-in, e prima del controllo passaporti, bisogna pagare CUC 25 a persona come imposta aereoportuale.</p>', '<p>S&iacute;, despu&eacute;s del chequeo, y antes del control de pasaportes, hay que pagar 25 CUC por persona como tasa de Aeropuerto.</p>', 26, '2012-11-12 17:29:30', 1),
(22, 'General', 'In che modo possiamo ricevere assistenza  in caso di problemi? ', '&iquest;C&oacute;mo podemos obtener ayuda en caso de problemas?', '<p>Contattando direttamente gli amministratori del sito:</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;Telefoni: 0052 999 2855910</p>\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 0052 999 588175&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<h3>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <a href="mailto:umberto@cubayucatan.mx">umberto@cubayucatan.m</a>x &nbsp;<span style="color: #6666ff;">info@cubayucatan.com</span></h3>\r\n<h3>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <a href="mailto:edelis@cubayucatan.mx">edelis@cubayucatan.mx</a></h3>\r\n<p>&nbsp;</p>\r\n<p>Comunque, per quanto riguarda Cuba, potete rivolgervi per qualsiasi problema ai gerenti delle Case, e avrete l&rsquo;assicurazione medica fin dalla partenza, all&rsquo;acquisto del biglietto aereo.</p>\r\n<p>In Yucat&aacute;n invece, sarete titolari di assicurazione personale, con telefono cellulare per mettervi in contatto con gli uffici predisposti.</p>', '<p>Contactando con los Administradores del sitio:</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp; Tel&eacute;fono: 0052 999 2855910</p>\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 0052 999 588175</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; umberto@cubayucatan.mx</p>\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; edelis@cubayucatan.mx</p>\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp;info@cubayucatan.com</p>\r\n<p>&nbsp;</p>\r\n<p>En Cuba, podr&aacute;n recurrir a los due&ntilde;os de las Casas, y adem&aacute;s tendr&aacute;n el seguro m&eacute;dico, &nbsp;que les proporcionar&aacute;n al momento de comprar el apsaje aereo.</p>\r\n<p>En Yucat&aacute;n, tendr&aacute;n seguro personal y tel&eacute;fono mobil incluidos en los precios.</p>', 14, '2012-11-14 09:40:38', 1),
(23, 'General', 'Quando riceveremo il Voucher, dopo aver pagato?', 'Cuando recibiremos el Voucher (comprobante) despu&eacute;s de pagar?', '<p>Immediatamente dopo aver verificato l&rsquo;arrivo del pagamento, tenendo conto della differenza oraria con l&rsquo;Italia (7 ore), e all&rsquo;indirizzo di posta elettronica che ci invierete con la prenotazione.&nbsp;</p>', '<p>Inmediatamente despu&eacute;s de confirmar la llegada del pago, teniendo en cuenta la diferencia horaria (7 horas) para los clientes europeos, a la direcci&oacute;n de correo electr&oacute;nico que nos enviar&aacute;n con la reservaci&oacute;n.</p>', 18, '2012-11-14 09:41:42', 1),
(24, 'General', 'I prezzi possono subire modifiche con il variare dei cambi fra le monete?', '&iquest;Los precios est&aacute;n sujetos a modificaciones con los tipos de cambio entre las monedas?', '<p>S&iacute;, sia per quanto riguarda il Peso convertibile cubano che il Peso messicano. Modificheremo automaticamente i prezzi in caso di variazioni, sia in rialzo che in ribasso. Nel sito avete a disposizione un bottone cambiavalute.</p>', '<p>S&iacute;, tanto por el Peso cubano Convertible(CUC) que por el Peso mexicano(MXP). Modificaremos autom&aacute;ticamente los precios en caso de cambios. En el sitio hay un bot&oacute;n de cambio divisas.</p>', 25, '2012-11-14 09:42:14', 1),
(25, 'General', 'Dove si trova la Societ&aacute; che gestisce il sito?', '&iquest;D&oacute;nde est&aacute; la Compa&ntilde;&iacute;a que administra el sitio?', '<p>La Societ&aacute; &eacute; la NUOVA G&Eacute;NESIS SRL de CV, Calle 70 n. 581/A, por 77&nbsp; y&nbsp; 79, M&eacute;rida Centro, Yucat&aacute;n, M&eacute;xico</p>\r\n<p>La societ&aacute; &eacute; regolarmente registrata presso le seguenti Istituzioni messicane:</p>\r\n<p>Secretar&iacute;a de Hacienda, con il Codice RFC ( Partita IVA ): NGE 080305 ETA</p>\r\n<p>Secretar&iacute;a de Turismo de Yucat&aacute;n.</p>\r\n<p>Ayuntamiento de la Ciudad de M&eacute;rida.</p>', '<p>La Compa&ntilde;&iacute;a es la NUOVA G&Eacute;NESIS&nbsp;SRL de CV, Calle 70 no. 581/A, Por 77 y 79, M&eacute;rida Centro, Yucat&aacute;n, M&eacute;xico</p>\r\n<p>La Compa&ntilde;&iacute;a se ha registrado en las siguientes Organizaciones P&uacute;blicas en M&eacute;xico:</p>\r\n<p>Secretar&iacute;a de Hacienda, con RFC (P. IVA): NGE 080305 ETA</p>\r\n<p>Secretar&iacute;a de Turismo de Yucat&aacute;n.</p>\r\n<p>Ayuntamiento de la Ciudad de M&eacute;rida.</p>', 27, '2012-11-14 09:42:38', 1),
(26, 'General', 'Chi sono e dove vivono gli Amministratori della Societ&aacute; e del sito?', '&iquest;Qui&eacute;nes son y d&oacute;nde viven, los Administradores de la Sociedad y de su sitio?', '<p>Gli Amministratori sono:</p>\r\n<p>Edelis Albolaez Valdivia, Umberto Fioroni y Pepe Rodriguez</p>\r\n<p>Edelis &eacute; cubana e Umberto vive da molti anni a Cuba e in Yucat&aacute;n, all&rsquo;indirizzo della Societ&aacute; che trovate qui sopra</p>\r\n<p>Pepe Rodriguez &eacute; yucateco e vive en M&eacute;rida.</p>', '<p>Los Administradores son:</p>\r\n<p>Edelis Albolaez Valdivia, Umberto Fioroni y Pepe Rodriguez</p>\r\n<p>Edelis es cubana y Umberto vive desde muchos a&ntilde;os en Cuba y Yucat&aacute;n.</p>\r\n<p>Pepe Rodriguez es yucateco, y vive en M&eacute;rida</p>', 17, '2012-11-14 09:43:13', 1),
(27, 'General', 'Che cosa significa Mayataan  e  Mayakot?', '&iquest;Qu&eacute; significan las palabras Mayataan y Mayakot? Y Ek Balam?', '<p>Mayataan significa Casa dei Maya, e Mayakot&nbsp; Confine dei Maya. Ek Balam significa Giaguaro Nero.</p>', '<p>Mayataan significa Casa de los Mayas, y &nbsp;Mayakot Frontera de los Mayas. Ek Balam significa Jaguar Negro.</p>', 24, '2012-11-14 09:43:31', 1),
(29, 'General', 'Che cosa sono le Casas Particulares?', '&iquest;Qu&eacute; son las Casas Particulares?', '<p>Sono Case Private di famiglie cubane, che hanno chiesto e ottenuto dal governo la licenza per affittare stanze ai turisti</p>', '<p>Son las viviendas particulares de familias cubanas, que han solicitado y obtenido del Gobierno una licencia para alquilar habitaciones a los turistas.</p>', 20, '2012-11-14 09:44:17', 1),
(30, 'General', 'Quali  forme di pagamento  accettate? E con quali modalit&aacute;?', '&iquest;Qu&eacute; formas de pago acepta cubayucatan.com? &iquest;Y en qu&eacute; modalidades?', '<p>Si accettano pagamenti con Bonifico Bancario, con carta di credito o con Pay Pal.&nbsp; I dettagli si trovano nella descrizione delle offerte turistiche.</p>', '<p>Aceptamos el pago por transferencia bancaria, tarjeta de cr&eacute;dito o Pay Pal. Los detalles se pueden encontrar en las descripciones &nbsp;de las ofertas tur&iacute;sticas.</p>', 30, '2012-11-14 09:44:36', 1),
(31, 'General', 'Che vantaggi ricavo dalla permanenza in Casas Particulares rispetto agli Alberghi?', 'Porqu&eacute; es mejor quedarse en Casas Particulares que en los Hoteles?', '<p>Le Casas Particulares sono molto pi&uacute; a buon mercato degli Alberghi.</p>\r\n<p>Inoltre, offrono lo splendido vantaggio di farvi vivere a stretto contatto con la vita quotidiana dei cubani, sia della famiglia che vi ospita che dei loro amici e vicini di quartiere.</p>\r\n<p>Non sono necessarie mance (se l&rsquo;iniziativa verr&aacute; da voi, ovviamente sar&aacute; ben accetta)</p>\r\n<p>I gerenti delle Case sono molto gentili e selezionati in anni di lavoro. Curano in modo speciale la pulizia e sono disponibili ad aiutarvi in qualsiasi modo in caso sorgessero problemi.</p>', '<p>Las Casas Particulares son mucho m&aacute;s baratas que los Hoteles.</p>\r\n<p>Tambi&eacute;n ofrecen la maravillosa&nbsp;ventaja &nbsp;de vivir en estrecho contacto con la vida cotidiana de los cubanos, tanto de la familia que les va a hospedar que de sus amigos y &nbsp;vecinos.</p>\r\n<p>Las propinas no son necesarias (si la iniciativa vendr&aacute; a ustedes, por supuesto, ser&aacute;n bienvenidas)</p>\r\n<p>Los due&ntilde;os de las Casas de &nbsp;nuestro programa son muy agradables, y han sido seleccionados en a&ntilde;os de trabajo. Cuidan de una manera especial la limpieza y est&aacute;n disponibles para ayudar en todo los problemas que puedan surgir.</p>', 28, '2012-11-14 09:44:55', 1),
(32, 'General', 'Quali  sono i vostri dati bancari?', '&iquest;Cu&aacute;les son sus datos bancarios?', '<p>Umberto Fioroni<br />Banco: Scotiabank<br />Sucursal: 001, Plaza 170, M&eacute;rida, Yucat&aacute;n, M&eacute;xico<br />Cuenta: 17001027751<br />Codigo Swift: MBCOMXMM</p>', '<p>Umberto Fioroni<br />Banco: Scotiabank<br />Sucursal: 001, Plaza 170, M&eacute;rida, Yucat&aacute;n, M&eacute;xico<br />Cuenta: 17001027751<br />Codigo Swift: MBCOMXMM</p>', 29, '2012-11-23 12:42:27', 1);

