--
-- Estructura de tabla para la tabla `testimonials`
--

CREATE TABLE IF NOT EXISTS `testimonials` (
  `id` int(10) unsigned NOT NULL auto_increment,
  `nombre` varchar(255) NOT NULL,
  `src` varchar(255) default '',
  `descripcion_ita` text,
  `descripcion_esp` text,
  `activo` tinyint(1) default '1',
  `orden` int(10) unsigned default '0',
  `created` datetime default NULL,
  PRIMARY KEY  (`id`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 AUTO_INCREMENT=7 ;

--
-- Volcado de datos para la tabla `testimonials`
--

INSERT INTO `testimonials` (`id`, `nombre`, `src`, `descripcion_ita`, `descripcion_esp`, `activo`, `orden`, `created`) VALUES
(1, 'Luca', 'upload/luca.jpg', '<p>&ldquo; Cuba &eacute; splendida e divertente, ho passato momenti bellissimi, la gente delle case private &eacute; stata gentile e premurosa, e anche quella del quartiere, certo ritorner&oacute;, grazie, Luca&rdquo;</p>', '<p>&ldquo; Cuba &eacute; splendida e divertente, ho passato momenti bellissimi, la gente delle case private &eacute; stata gentile e premurosa, e anche quella del quartiere, certo ritorner&oacute;, grazie, Luca&rdquo;</p>', 1, 1, '2012-10-25 17:17:33'),
(2, 'Gabriele', 'upload/gabriele.jpg', '<p>&ldquo; Mare e cultura maya, bellissimo, abbiamo fatto un gran bel giro, e con la benzina cos&iacute; a buon mercato poi ! grazie, indimenticabile&nbsp;</p>', '<p>&ldquo; Mare e cultura maya, bellissimo, abbiamo fatto un gran bel giro, e con la benzina cos&iacute; a buon mercato poi ! grazie, indimenticabile&nbsp;</p>', 1, 2, '2012-10-25 17:18:26'),
(3, 'Mariana', 'upload/mariana.jpg', '<p>&ldquo; Increible la experiencia &nbsp;en Yucat&aacute;n, todo salio muy bien, es un viaje que no olvidare&rdquo;</p>', '<p>&ldquo; Increible la experiencia &nbsp;en Yucat&aacute;n, todo salio muy bien, es un viaje que no olvidare&rdquo;</p>', 1, 3, '2012-10-25 17:19:02'),
